//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	if ( errCode == 0 && ds_list.RowCount() > 0) {
		var user_grp = gfn_getUserGroup();

		if(user_grp == "10" || user_grp == "30") { // 보수협력사, 직영일때만 발송 버튼을 enable 한다.
			ufn_enableBtn("trans", true);
		} else {
			ufn_enableBtn("trans", false);
		}
	} else {
		ufn_enableBtn("trans", false);
	}
}

function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}

/********************************************************************************
* 기      능   : 발송 Callback  
********************************************************************************/
function fn_afterTrans(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	ufn_enableBtn("save", false);

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query2(cRow);
	}
}

/********************************************************************************
* 기      능   : 저장2 Callback  
********************************************************************************/
function fn_afterSave2(errCode, errMsg) {
	if (errMsg == "NO_DATA_FOUND") {
		gfn_showAlert("CW00153");
	} else if (errMsg == "INVALID_WBS") {
		gfn_showAlert("CW00154");
	} else if (errMsg == "INVALID_LIF1") {
		gfn_showAlert("CW00155");
	} else if (errMsg == "NO_DATA_WBS") {
		gfn_showAlert("CW00156");
	} else if (errMsg == "NO_DATA_LIF1") {
		gfn_showAlert("CW00157");
	} else if (errMsg == "NO_DATA_GUBUN") {
		gfn_showAlert("CW00158");
	} else if (ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		// error 정보 표시 
		gfn_openWSErrorWin(grd_list2, ds_list4, ds_error, "FLAG");
	} else {
		if (errCode != 0) {
			gfn_showMsg("CE00001");
		} else {
			gfn_showMsg("CI00005");
			fn_query2(cRow);
		}
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");
		fn_query2(cRow);
	}
}

