	gfn_clearMsgBar();

	if(strColumnID == "CHK") {
		obj.fireevent = false;
		if(ds_list.GetColumn(nRow, "CHK") == true) {
			ds_list.SetColumn(nRow, "FLAG", "U");
		} else {
			ds_list.SetColumn(nRow, "FLAG", "");
		}
		obj.fireevent = true;
	}
}

function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
//	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
	gfn_clearMsgBar();

	if(strColumnID == "CHK") {
		if ( gfn_isNull(Tab00.tab2.cal_baljudate_in, true) ) {
			var arr = [Tab00.tab2.st_baljudate_in.value];
			gfn_showMsg("CW00001", arr);
			obj.fireevent = false;
			ds_list2.SetColumn(nRow, "CHK", false);
			obj.fireevent = true;
			return;
		}
/*
		if ( gfn_isNull(Tab00.tab2.ed_upche_in, true) ) {
			var arr = [Tab00.tab2.st_upche_in.value];
			gfn_showMsg("CW00001", arr);
			obj.fireevent = false;
			ds_list2.SetColumn(nRow, "CHK", false);
			obj.fireevent = true;
			return;
		}
*/
		if(cb_daesang.value == "B") { // 무상일반
			if ( gfn_isNull(Tab00.tab2.cal_ipjudate_in, true) ) {
				var arr = [Tab00.tab2.st_ipjudate_in.value];
				gfn_showMsg("CW00001", arr);
				obj.fireevent = false;
				ds_list2.SetColumn(nRow, "CHK", false);
				obj.fireevent = true;
				return;
			}

			if(Tab00.tab2.cal_ipjudate_in.value < Tab00.tab2.cal_baljudate_in.value) {
				var arr = [Tab00.tab2.st_ipjudate_in.value, Tab00.tab2.st_baljudate_in.value];
				gfn_showMsg("CW00036", arr);
				obj.fireevent = false;
				ds_list2.SetColumn(nRow, "CHK", false);
				obj.fireevent = true;
				return;
			}
		}

		if(ds_list2.GetColumn(nRow, "CHK") == true && length(ds_list2.GetColumn(nRow, "PST")) == 0) {
			if(ds_list2.GetColumn(nRow, "A_CNT") > 0) {
//				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
//				gfn_showMsg("CW00083", arr);
				ds_list2.row = nRow;
//				return;
			}
			if(ds_list2.GetColumn(nRow, "B_CNT") > 0) {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00084", arr);
				ds_list2.row = nRow;
				return;
			}
/* 2014.03.04 LJH 무상공사/유상공사 프로세스 변경(유상공사이후에 무상공사 가능)
			if(ds_list2.GetColumn(nRow, "C_CNT") >= Tab00.tab2.cal_baljudate_in.value) {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00085", arr);
				ds_list2.row = nRow;
				return;
			}
*/
			if(ds_list2.GetColumn(nRow, "M_MON") == 0 && cb_daesang.value == "B" && cb_gubun.value == "A") {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00173", arr);
				ds_list2.row = nRow;
				return;
			}

			if(cb_daesang.value == "B") { // 무상일반
				if(ds_list2.GetColumn(nRow, "TBMT") >= Tab00.tab2.cal_baljudate_in.value) {
					ds_list2.SetColumn(nRow, "CHK", false);
					var arr = [nRow + 1];
					gfn_showMsg("CW00057", arr);
					ds_list2.row = nRow;
					return;
				}
			}
		}
		if(ds_list2.GetColumn(nRow, "CHK") == true) {
			var pjt_gbn = parseInt(ds_list2.GetColumn(nRow, "PJT_GBN"));
			var hno_gbn = parseInt(ds_list2.GetColumn(nRow, "HNO_GBN"));

			if(pjt_gbn > 0 && hno_gbn > 0) {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00089", arr);
				ds_list2.row = nRow;
				return;
			}
			if(pjt_gbn > 0 && hno_gbn == 0) {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00090", arr);
				ds_list2.row = nRow;
				return;
			}
			if(pjt_gbn == 0 && hno_gbn > 0) {
				ds_list2.SetColumn(nRow, "CHK", false);
				var arr = [nRow + 1];
				gfn_showMsg("CW00091", arr);
				ds_list2.row = nRow;
				return;
			}
		}
		obj.fireevent = false;
		if(ds_list2.GetColumn(nRow, "CHK") == true) {
			ds_list2.SetColumn(nRow, "FLAG", "U");
		} else {
			ds_list2.SetColumn(nRow, "FLAG", "");
		}
		obj.fireevent = true;
	}
}

function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function cb_gubun_OnChange() {
	if(cb_gubun.value == "A") {
//		cal_balju_fm.Enable = false;
//		cal_balju_to.Enable = false;
		cb_pst.Enable = false;
	} else {
//		cal_balju_fm.Enable = true;
//		cal_balju_to.Enable = true;
		cb_pst.Enable = true;
	}
}

function Tab00_OnChanged(obj, nOldIndex, nNewIndex) {
	var user_grp = gfn_getUserGroup();

	if(obj.TabIndex == 0) { // 무상발주청구등록
		ufn_enableBtn("query", true);
		if(user_grp == "10" || user_grp == "30") { // 보수협력사,직영일때만 발송 버튼을 enable 한다.
			ufn_enableBtn("trans", true);
		}

		ufn_enableBtn("save", false);
		ufn_enableBtn("delete", false);

		ed_pjt.Enable = true;
		btn_pjt.Enable = true;
		cb_gubun.Enable = true;
		cb_daesang.Enable = true;
		div_code.cb_big.Enable = false;
		div_code.cb_mid.Enable = false;
		if(user_grp == "10") {
			div_code.cb_small.Enable = false;
		} else {
			div_code.cb_small.Enable = true;
		}

		if(cb_gubun.value == "A") {
//			cal_balju_fm.Enable = false;
//			cal_balju_to.Enable = false;
		} else {
//			cal_balju_fm.Enable = true;
//			cal_balju_to.Enable = true;
		}

		btn_balju.Enable = false;

		cRow = null;
		fn_query();
	} else if(obj.TabIndex == 1) { // 무상발주청구등록상세
		ufn_enableBtn("query", false);
		ufn_enableBtn("trans", false);

		if(user_grp == "10" || user_grp == "30" || G_USER_ID == "2021200" || 
		   G_USER_ID == "2021153" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") { // 보수협력사,직영일때만 저장,삭제 버튼을 enable 한다.
			ufn_enableBtn("save", true);
			ufn_enableBtn("delete", true);
		} else {
			ufn_enableBtn("save", false);
			ufn_enableBtn("delete", false);
		}

		ed_pjt.Enable = false;
		btn_pjt.Enable = false;
		cb_gubun.Enable = false;
		cb_daesang.Enable = false;
		div_code.cb_big.Enable = false;
		div_code.cb_mid.Enable = false;
		div_code.cb_small.Enable = false;

		if(G_USER_ID == "2017938" || G_USER_ID == "2017750") {
			btn_balju.Enable = true;
		} else {
			btn_balju.Enable = false;
		}

//		cal_balju_fm.Enable = false;
//		cal_balju_to.Enable = false;

		cRow = ds_list.GetCurrow();
		fn_query2(cRow);
	}
}

function Tab00_OnChanging(obj,nOldIndex,nNewIndex) {
	if(ds_list.rowCount() == 0) {
		gfn_showMsg("CW00010");
		return false;
	}
}

