function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	cal_balju_fm.value = firstDay;
	cal_balju_to.value = gfn_getCurrDate();
	cal_balju_fm.SetFocus();

	var codeArr = ["CS117"];
	var dsObjArr = [ds_pst_cd];
	var addTypeArr = [G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	var user_grp = gfn_getUserGroup();

//	if(user_grp <> "20") {
//		ufn_enableBtn("query", false);
//	}

	ufn_enableBtn("receipt", false);
	ufn_enableBtn("sendback", false);
	ufn_enableBtn("delete", false);
	ufn_enableBtn("save", false);

	Tab00.tab1.SetFocus();
}
