/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if ( gfn_isNull(cal_balju_fm, true) ) {
		var arr = [st_balju_date.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if ( gfn_isNull(cal_balju_to, true) ) {
		var arr = [st_balju_date.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	if ( gfn_isNull(cb_pst, true) ) {
		var arr = [st_pst.value];
		gfn_showMsg("CW00001", arr);
		return;
	}

	var user_grp = gfn_getUserGroup();

	if(user_grp == "40") { // 고객지원부일 경우
		if(length(div_code.cb_big.value) == 0) {
			if(length(div_code.cb_mid.value) == 0) {
				if(length(div_code.cb_small.value) == 0) {
					var arr = [st_bsu.value];
					gfn_showMsg("CW00001", arr);
					return;
				}
			}
		} else {
			if(length(div_code.cb_mid.value) == 0) {
				if(length(div_code.cb_small.value) == 0) {
					var arr = [st_bsu.value];
					gfn_showMsg("CW00001", arr);
					return;
				}
			}
		}
	}

	ds_cond.SetColumn(0, "BALJU_FM", cal_balju_fm.value);
	ds_cond.SetColumn(0, "BALJU_TO", cal_balju_to.value);
	ds_cond.SetColumn(0, "PST", cb_pst.value);
	ds_cond.SetColumn(0, "GND", cb_gnd.value);

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(Tab00.tab1.grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs11:CS1103001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

function fn_query2(cRow) {
	ds_cond2.SetColumn(0, "GBN", trim(ds_list.GetColumn(cRow,"GND")));
	ds_cond2.SetColumn(0, "PJT", trim(ds_list.GetColumn(cRow,"PJT")));
	ds_cond2.SetColumn(0, "BSU", trim(ds_list.GetColumn(cRow,"BSU")));
	ds_cond2.SetColumn(0, "AGB", trim(ds_list.GetColumn(cRow,"AGB")));

	// sort mark 삭제 
	gfn_clearGridSortMark(Tab00.tab2.grd_list2);
	ds_list2.row = -1;
	ds_list2.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs11:CS1103001A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
