function fn_sendback() {
	if ( ds_list.rowCount() < 1 ) {
		// 접수할 정보 없음. 
		return;
	}

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "PST") <> 'A3' && ds_list.GetColumn(i, "PST") <> 'A5') { //발송, 접수상태만 반송 가능
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				ds_list.row = i;
				return;
			}
		}
	}

	tit_clearActionInfo();

    // 무상보수 발주정보 update(zcst116) 
	tit_addSaveActionInfo(
        ""
        , "cs11:CS1103001A_U2"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list"
        , ""
        , ""
        , "fn_afterSendback");
}
