// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	if ( errCode == 0 && ds_list.RowCount() > 0) {
		var user_grp = gfn_getUserGroup();

		if(user_grp == "20" || G_USER_ID = "2020971" || G_USER_ID = "2021153") { // 보수PM일때만 접수, 반송 버튼을 enable 한다.
			ufn_enableBtn("receipt", true);
			ufn_enableBtn("sendback", true);
		} else if(user_grp == "40" && G_VEND_CODE == '0000009935' && G_USER_ID == '3017079') { //주차사업부 : 강명수직장(주차PM역할)
			ufn_enableBtn("receipt", true);
			ufn_enableBtn("sendback", true);
		} else {
			ufn_enableBtn("receipt", false);
			ufn_enableBtn("sendback", false);
		}
	} else {
		ufn_enableBtn("receipt", false);
		ufn_enableBtn("sendback", false);
	}
}

function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query2(cRow);
	}
}

/********************************************************************************
* 기      능   : 접수 Callback  
********************************************************************************/
function fn_afterReceipt(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 반송 Callback  
********************************************************************************/
function fn_afterSendback(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}
