/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
//	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
	gfn_clearMsgBar();

	if(strColumnID == "CHK") {
		obj.fireevent = false;
		if(ds_list.GetColumn(nRow, "CHK") == true) {
			ds_list.SetColumn(nRow, "FLAG", "U");
		} else {
			ds_list.SetColumn(nRow, "FLAG", "");
		}
		obj.fireevent = true;
	}
}
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
//	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
	gfn_clearMsgBar();

	if(strColumnID == "CHK") {
		obj.fireevent = false;
		if(ds_list2.GetColumn(nRow, "CHK") == true) {
			ds_list2.SetColumn(nRow, "FLAG", "U");
		} else {
			ds_list2.SetColumn(nRow, "FLAG", "");
		}
		obj.fireevent = true;
	}
}

function Tab00_OnChanged(obj, nOldIndex, nNewIndex)
{
	var user_grp = gfn_getUserGroup();

	if(obj.TabIndex == 0) { // 무상발주청구접수
		ufn_enableBtn("query", true);

		if(user_grp == "20" || G_USER_ID = "2020971"|| G_USER_ID = "2021153") { // 보수PM일때만 접수, 반송 버튼을 enable 한다.
			ufn_enableBtn("receipt", true);
			ufn_enableBtn("sendback", true);
		} else if(user_grp == "40" && G_VEND_CODE == '0000009935' && G_USER_ID == '3017079') { //주차사업부 : 강명수직장(주차PM역할)
			ufn_enableBtn("receipt", true);
			ufn_enableBtn("sendback", true);
		}

		ufn_enableBtn("delete", false);
		ufn_enableBtn("save", false);

		cal_balju_fm.Enable = true;
		cal_balju_to.Enable = true;
		cb_pst.Enable = true;
		div_code.cb_big.Enable = false;
		div_code.cb_mid.Enable = false;
		div_code.cb_small.Enable = true;

		cRow = null;
		fn_query();
	} else if(obj.TabIndex == 1) { // 무상발주청구접수상세
		ufn_enableBtn("query", false);
		ufn_enableBtn("receipt", false);
		ufn_enableBtn("sendback", false);

		if(user_grp == "20"  || G_USER_ID = "2020971"|| G_USER_ID = "2021153") { // 보수PM일때만 저장,삭제 버튼을 enable 한다.
			ufn_enableBtn("delete", true);
			ufn_enableBtn("save", true);
		} else if(user_grp == "40" && G_VEND_CODE == '0000009935' && G_USER_ID == '3017079') { //주차사업부 : 강명수직장(주차PM역할)
			ufn_enableBtn("delete", true);
			ufn_enableBtn("save", true);
		} else {
			ufn_enableBtn("delete", false);
			ufn_enableBtn("save", false);
		}

		cal_balju_fm.Enable = false;
		cal_balju_to.Enable = false;
		cb_pst.Enable = false;
		div_code.cb_big.Enable = false;
		div_code.cb_mid.Enable = false;
		div_code.cb_small.Enable = false;

		cRow = ds_list.GetCurrow();
		fn_query2(cRow);
	}
}
function Tab00_OnChanging(obj,nOldIndex,nNewIndex)
{
	if(ds_list.rowCount() == 0) {
		gfn_showMsg("CW00010");
		return false;
	}
}

function Tab00_tab2_btn_upche_OnClick(obj)
{
	var arg;
	gfn_openPopup("CS1102001C",true,arg);
}
