function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var yymm = substr(gfn_getCurrDate(),0,6);
//	med_yymm.value = yymm;

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-14,5);

	var user_grp = gfn_getUserGroup();

	if( user_grp == "20" || user_grp == "30" || user_grp == "40" ) {
		ufn_enableBtn("save",   true);
	} else {
		ufn_enableBtn("save",   false);
	}

	ed_upn.SetFocus();

	var codeArr = ["CS179", "CS182", "CURST"];
	var dsObjArr = [ds_hep_cd, ds_ctp_cd, ds_curst];
	var addTypeArr = [G_CODE_NONE,G_CODE_SELECT,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_ctp_cd.Filter(" CODE!=" +quote("B") + "&&CODE!="+quote("D"));

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1201001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	input_auth01 = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	//==================CRM 전환 관련 안내문구 추가 2021.05.13 HJH================================
	gfn_showAlert("CI30001", "5월 10일부터는 S-CRM으로 서비스 영업 계약을 진행요청 드리며," +
							 "\n\n개선 요청 및 문제점에 대하여서는 기존과 같이 BS포탈 우측 상단 메인 메뉴 " + 
							 "\n\n[문의/개선 요청 게시판]에 자유로운 피드백 작성 및 공지사항의 담당자 에게 발송 송부 바랍니다. " +
							 "\n5월 17일 부터는 SRM의 계약메뉴 사용은 각 지역 담당별로 순차적으로 확대하여 중단 진행 예정입니다."

				  );	
	//============================================================================================		
}

/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_fmy_cd.AddRow();
		ds_fmy_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_fmy_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum)));
		row++;
	}
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {

	Tab00.tab1.SetFocus();
	cb_gbn.Index = 0;
	chk_confirm.Value = 0;
	Tab00.tab3.Enable = false;
	gfn_showMsg("CI00007");
}

