/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	//구분 필드 필수체크	
	if(length(med_yymm.value) <> 0) {
		if(gfn_isNull(cb_gbn, true, st_gbn.value))
			return;
	}

	// 필수 입력 항목 체크 
	if(user_grp == "40") { // 고객지원부일 경우
		if ( gfn_isNull(ed_upn, true) ) {
			if(length(div_code.cb_big.value) == 0 && length(div_code.cb_mid.value) == 0) {
				var arr = [st_upn.value, st_bsu.value];
				gfn_showMsg("CW00045", arr);
				return;
			}
		}
	}

	ds_cond.SetColumn(0, "UPN", ed_upn.value);
	ds_cond.SetColumn(0, "CST", ed_cst.value);
	/* 20120905 LHR 제품구분 추가 (이선재SW 요청) */
	if(cb_pbn.value <> 0) { 
		ds_cond.SetColumn(0, "PBN", cb_pbn.Value);
	} else{
		ds_cond.SetColumn(0, "PBN", "");
	}


	//=========오류수정 : 구분값 선택시 기준년월 없이  SQL  실행시 시스템 부화 발생 으로 추가===========

	if( length(cb_gbn.value) > 0 && length(med_yymm.value) <> 6 )
	{
		var arr = [st_yymm.value, st_yymm.value];		gfn_showMsg("CW00045", arr);		return;
	}

	if( length(ed_upn.value) == 0 && (length(med_yymm.value) <> 6 || length(cb_gbn.value) == 0) )
	{
		var arr = [st_yymm.value, st_gbn.value];		gfn_showMsg("CW00045", arr);		return;
	}	
	//==================================================================================================


	if(length(med_yymm.value) <> 0) {
		ds_cond.SetColumn(0, "YYMM", med_yymm.value);
		ds_cond.SetColumn(0, "YYMM2", gfn_getBeforeMonth(med_yymm.value, 1));
		ds_cond.SetColumn(0, "YYMM3", gfn_getAfterMonth(med_yymm.value, 1));
		ds_cond.SetColumn(0, "YYMM4", gfn_getAfterMonth(med_yymm.value, 3));
		ds_cond.SetColumn(0, "GBN", cb_gbn.value);
	} else {
		ds_cond.SetColumn(0, "YYMM", "");
		ds_cond.SetColumn(0, "YYMM2", "");
		ds_cond.SetColumn(0, "YYMM3", "");
		ds_cond.SetColumn(0, "YYMM4", "");
		ds_cond.SetColumn(0, "GBN", "");
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		//====================================수정시작=======================================	
		/*
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
		*/
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else if(div_code.cb_big.value == '3') { // 협력사 콤보1이 팀 
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));

		} else { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", substr(trim(div_code.cb_mid.value),0,2));
			ds_cond.SetColumn(0, "BSU", '');
		}		
		//====================================수정끝=======================================	
	}

	if(length(ed_upn.value) <> 0) { // 통합프로젝트 번호를 입력했을 경우에 upn, cst만 조건으로 넘긴다.
		ds_cond.SetColumn(0, "UPN", ed_upn.value);
		ds_cond.SetColumn(0, "CST", ed_cst.value);
		/*
		if(user_grp == "30") { // 지방사무소일 경우
			ds_cond.SetColumn(0, "ARA", G_AREA_CODE);
		} else {
			ds_cond.SetColumn(0, "ARA", '');
		}
		*/
		if(user_grp == "40") { // 고객지원부일 경우
			ds_cond.SetColumn(0, "BSU", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			//=============추가=============
			ds_cond.SetColumn(0, "DPT", '');
			//==============================
		}
	}

	// 조회 조건이 모두 공백인 경우는 메세지 처리
	if(ds_cond.GetColumn(0,"UPN") == "" && ds_cond.GetColumn(0,"BSU") == "" && ds_cond.GetColumn(0,"TEM") == "" && ds_cond.GetColumn(0,"BPM") == "" && ds_cond.GetColumn(0,"ARA") == ""
	   //=============추가=============
	   && ds_cond.GetColumn(0,"DPT") == ""
	   //==============================
	  ) 
	{   
		var arr = [st_upn.value, st_bsu.value];
		gfn_showMsg("CW00045", arr);
		return;
	}

	gRow = null;
	// sort mark 삭제 
	gfn_clearGridSortMark(Tab00.tab1.grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	var sql;

	if(chk_confirm.Value == "1") {
		sql = "cs12:CS1201001A_S5"; // 미승인건 조회시
	} else {
		if(cb_gbn.value == "2") { // 구분값이 재계약대상일 경우
			sql = "cs12:CS1201001A_S3";
		} else if(cb_gbn.value == "3") { // 구분값이 On-Hand현황일 경우
			sql = "cs12:CS1201001A_S4";
		} else {
			if(length(ed_upn.value) <> 0) { // 통합프로젝트 번호 입력시
				sql = "cs12:CS1201001A_S1";
			} else {
				sql = "cs12:CS1201001A_S2"; // 구분값이 월별계약현황일 경우
			}
		}
	}

	var s_ara   = length(trim(ds_cond.GetColumn(0, "ARA")));
	var s_bpm   = length(trim(ds_cond.GetColumn(0, "BPM")));
	var s_tem   = length(trim(ds_cond.GetColumn(0, "TEM")));
	var s_bsu   = length(trim(ds_cond.GetColumn(0, "BSU")));
	var s_upn   = length(trim(ds_cond.GetColumn(0, "UPN")));
	var s_cst   = length(trim(ds_cond.GetColumn(0, "CST")));
	var s_yymm  = length(trim(ds_cond.GetColumn(0, "YYMM")));
	var s_yymm2 = length(trim(ds_cond.GetColumn(0, "YYMM2")));
	var s_yymm3 = length(trim(ds_cond.GetColumn(0, "YYMM3")));
	var s_yymm4 = length(trim(ds_cond.GetColumn(0, "YYMM4")));
	var s_gbn   = length(trim(ds_cond.GetColumn(0, "GBN")));
	//==========================추가==========================
	var s_dpt   = length(trim(ds_cond.GetColumn(0, "DPT")));
	//========================================================
	if(chk_confirm.Value != "1") {
		if(s_ara == 0 && s_bpm == 0 && s_tem == 0 && s_bsu == 0 && s_upn == 0 && s_cst == 0 
			&& s_yymm == 0 && s_yymm2 == 0 && s_yymm3 == 0 && s_yymm4 == 0 && s_gbn == 0 
			//=============추가=============
			&& s_dpt == 0
			//==============================
		  ) {
			gfn_showAlert("조건입력항목이 불일치 합니다.\n화면을 재오픈하여 사용하시기 바랍니다.");
			return;
		}
	}
	ds_ctp_cd.Filter(" CODE!=" +quote("B") + "&&CODE!="+quote("D"));
	tit_clearActionInfo();
	tit_addSearchActionInfo(sql, null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list_decrypt=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

