/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();
	ds_list2.ClearData();

	for(var i=0; i<ds_list.RowCount(); i++) {
		var org_rm2 = ds_list.GetOrgColumn(i, "RM2");
		var tmp_rm2 = ds_list.GetColumn(i, "RM2");

		var org_cyn = ds_list.GetOrgColumn(i, "CYN");
		var tmp_cyn = ds_list.GetColumn(i, "CYN");

		var org_fmy = ds_list.GetOrgColumn(i, "FMY");
		var tmp_fmy = ds_list.GetColumn(i, "FMY");

		var org_fms = ds_list.GetOrgColumn(i, "FMS");
		var tmp_fms = ds_list.GetColumn(i, "FMS");
		//점검주기 추가 20130222 LHR
		var org_hep = ds_list.GetOrgColumn(i, "HEP");
		var tmp_hep = ds_list.GetColumn(i, "HEP");

		var org_ctp = ds_list.GetOrgColumn(i, "CTP");
		var tmp_ctp = ds_list.GetColumn(i, "CTP");

		var org_cfg = ds_list.GetOrgColumn(i, "CFG");
		var tmp_cfg = ds_list.GetColumn(i, "CFG");

		//장기계약연도,차수 추가 2023687 용아름
		var org_lty = ds_list.GetOrgColumn(i, "LTY");
		var tmp_lty = ds_list.GetColumn(i, "LTY");

		var org_lts = ds_list.GetOrgColumn(i, "LTS");
		var tmp_lts = ds_list.GetColumn(i, "LTS");



		if(org_rm2 <> tmp_rm2 || org_cyn <> tmp_cyn || org_fmy <> tmp_fmy || org_fms <> tmp_fms || org_hep <> tmp_hep || org_ctp <> tmp_ctp || org_cfg <> tmp_cfg || org_lty <> tmp_lty || org_lts <> tmp_lts) {
			var rows = ds_list2.AddRow();
			ds_list2.SetColumn(rows, "FLAG", "U");
			ds_list2.SetColumn(rows, "UPN", ds_list.GetColumn(i, "UPN"));
			ds_list2.SetColumn(rows, "CST", ds_list.GetColumn(i, "CST"));
			ds_list2.SetColumn(rows, "GNO", ds_list.GetColumn(i, "GNO"));
			ds_list2.SetColumn(rows, "RM2", ds_list.GetColumn(i, "RM2"));
			ds_list2.SetColumn(rows, "CYN", ds_list.GetColumn(i, "CYN"));
			ds_list2.SetColumn(rows, "FMY", ds_list.GetColumn(i, "FMY"));
			ds_list2.SetColumn(rows, "FMS", ds_list.GetColumn(i, "FMS"));
			ds_list2.SetColumn(rows, "HEP", ds_list.GetColumn(i, "HEP"));
			ds_list2.SetColumn(rows, "CTP", ds_list.GetColumn(i, "CTP"));
			ds_list2.SetColumn(rows, "CFG", ds_list.GetColumn(i, "CFG"));
			ds_list2.SetColumn(rows, "LTY", ds_list.GetColumn(i, "LTY"));
			ds_list2.SetColumn(rows, "LTS", ds_list.GetColumn(i, "LTS"));
		}
	}

	if(ds_list2.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        ""
        , "cs12:CS1201001A_U1"
        , ""
        , "FLAG");

	tit_addSaveActionInfo(
        ""
        , "cs12:CS1201001A_U2"
        , ""
        , "FLAG");

	  //장기계약 수정 2023687 용아름 
    tit_addSaveActionInfo(
        ""
        , "cs12:CS1201001A_U3"
        , ""
        , "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2"
        , ""
        , ""
        , "fn_afterSave");
}

