//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

   // 복호화 적용 201610 이하림
   // DEC_COL 복호화 대상
   // DEC_TYP  타입

	tit_callService(
         "COMM_ACT"
        , "getDsDecrypt"
        , "ds_dec=ds_list_decrypt"
        , "ds_list=ds_list"
        , "DEC_COL=CS121_GNO DEC_TYP=0"
        , "fn_afterDecrypt"
        , true);

 }
function fn_afterDecrypt(errCode, errMsg) {

	//FM 연도, 차수 입력 제한 _ 20121204 _ LHR
    //LTY 연도, 차수 입력 제한 _ 20140709 용아름
    //점검주기 직영의 경우는 수정 불가하도록 그리드에서 처리 20150316
	if(input_auth01 == true){
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","FMY"),"Edit","combo");	//FMY
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","FMS"),"Edit","combo");	//FMS
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","HEP"),"Edit","combo");	//점검주기
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","LTY"),"Edit","combo");	//LTY
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","LTS"),"Edit","combo");	//LTS
	} else {	
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","FMY"),"Edit","none");	//FMY	
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","FMS"),"Edit","none");	//FMS
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","HEP"),"Edit","none");	//점검주기
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","LTY"),"Edit","none");	//LTY
		Tab00.tab1.grd_list.SetCellProp("body",Tab00.tab1.grd_list.GetBindCellIndex("body","LTS"),"Edit","none");	//LTS
	}

	paramUpn = "";
	paramCst = "";
	paramGno = "";

	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

