/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID == "CHK") {
		if(ds_list.getColumn(nRow, "CHK") == "1") {
			if(gRow != null) {
				ds_list.SetColumn(gRow, "CHK", "0");
			}

			gRow = nRow;

			var arg = "upn=" + quote(ds_list.GetColumn(nRow, "UPN")) + 
					  " cst=" + quote(ds_list.GetColumn(nRow, "CST")) + 
					  " nam=" + quote(ds_list.GetColumn(nRow, "NAM")) + 
					  " spt=" + quote(ds_list.GetColumn(nRow, "SPT")) + 
					  " gnd=" + quote(ds_list.GetColumn(nRow, "GND")) +
					  " usd=" + quote(ds_list.GetColumn(nRow, "USD")) + 
					  " uhj=" + quote(ds_list.GetColumn(nRow, "UHJ")) + 
					  " gno=" + quote(ds_list.GetColumn(nRow, "GNO")) + 
					  " vbeln=" + quote(ds_list.GetColumn(nRow, "VBELN"));

			gfn_openPopup("CS1201001B", true, arg);
		} else {
			if(gRow == nRow) {
				gRow = null;
			}
		}
	}

	if(strColumnID == "CYN") {
		if(ds_list.GetColumn(nRow, "CYN") <> "Y"){
			ds_list.SetColumn(nRow, "CTP", "");	
			ds_list.SetColumn(nRow, "CFG", "");	
		}
	}
	if(strColumnID == "CTP") {
		if(length(ds_list.GetColumn(nRow, "CTP")) > 0 && ds_list.GetColumn(nRow, "CYN") <> "Y"){
			ds_list.SetColumn(nRow, "CTP", "");	
			ds_list.SetColumn(nRow, "CFG", "");	
		}
	}
	if(strColumnID == "CFG") {
		if(ds_list.GetColumn(nRow, "CFG") == "Y" && ds_list.GetColumn(nRow, "CYN") <> "Y"){
			ds_list.SetColumn(nRow, "CTP", "");	
			ds_list.SetColumn(nRow, "CFG", "");	
		}
	}

}

