/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_cond.SetColumn(0,"GNO", gno);
	ds_cond.SetColumn(0,"TEM", tem);
	ds_cond.SetColumn(0,"RDT", rdt);
	ds_cond.SetColumn(0,"SEQ", seq);
	ds_cond.SetColumn(0,"UPN", upn);
	ds_cond.SetColumn(0,"CST", cst);
	ds_cond.SetColumn(0,"DOC", fa_docno);
	ds_cond.SetColumn(0,"GBN", fa_popgbn);

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs12:CS1201001H_S5"); // 중도해지 진행 이력 체크

	if( fa_popgbn == "D" ||fa_popgbn == "C" ) {   							    // 상세 및 중도해지 현황 조회시
		tit_addSearchActionInfo("cs12:CS1201001H_S1"); // 호기별 유상정보
		tit_addSearchActionInfo("cs12:CS1201001H_S2"); // 중도해지 요청 내용
		tit_addSearchActionInfo("cs12:CS1201001H_S6"); // 중도해지 처리 대상
	} else { 										   							// 결재문서함 부서문서함 조회시
		tit_addSearchActionInfo("cs12:CS1201001H_S4"); // 호기별 유상정보 - zcst630 기준
		tit_addSearchActionInfo("cs12:CS1201001H_S2"); // 중도해지 요청 내용
		tit_addSearchActionInfo("cs12:CS1201001H_S7"); // 중도해지 처리 대상
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list2=ds_list ds_zcst630=ds_zcst630 ds_list_0=ds_list_0 ds_gno=ds_gno ds_list3=ds_list3"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	ds_cond.SetColumn(0,"DOC", ed_doc2.value);

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1201001H_S3"); // 결재자 정보	
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list_0=ds_list_0"
        , ""
        , "fn_setBtn"
        , true);
}

/********************************************************************************
* 기      능   :  조회 후 버튼처리  
********************************************************************************/
function fn_setBtn(errCode, errMsg) {

	div_btn.btn_save.enable = false ; //저장
	div_btn.btn_draft.enable = false ; //기안
	div_btn.btn_decide.enable = false ; //결재
	div_btn.btn_sendback.Enable = false ; //반송
	div_btn.btn_delete.enable = false ;   //삭제 
	div_btn.btn_rewrite.enable = false ;  //재작성

	if((G_USER_ID == "2021200" || G_USER_ID == "2023687" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") && ds_zcst630.GetColumn(0, "CS630_PST") == "A1" ) {
		btn_testApproval.Visible = true ;
	} else {
		btn_testApproval.Visible = false ;
	}

	if(length(ed_doc2.value) != 0 && length(ed_apstat_nm.value) != 0 ) {
		cb_crs.Enable = false;
		cal_cdt.Enable = false;
		ed_dxt.Enable = false;
		ed_gxt.Enable = false;
		ed_hxt.Enable = false;
		cal_rpd.Enable = false;
		ed_rpl.Enable = false;
	}

	if(ds_list2.rowcount == 0 && fa_popgbn == "D") {
		gfn_showAlert("해당 계약은 중도해지 대상이 아닙니다.");
		return;
	} 

	if(ds_gno.rowcount > 0 && ds_list2.GetColumn(0, "UD_GUBUN") == "N") {
		gfn_showAlert(ds_gno.GetColumn(0,"CS630_GNO")+" 계약이 중도해지 진행 중입니다.");
		return;
	}

	if(length(ed_doc.value) == 0) { // 관리번호 없는 경우 >> 중도해지 작성 내역 없음
		ds_zcst630.AddRow(); 
		cal_mdt.Value = gfn_getCurrDate();
	}	
	//초기 작성 및 수정 , 삭제
	if(length(ds_zcst630.GetColumn(0,"APSTAT")) == 0 && fa_popgbn == "D"){
		div_btn.btn_save.enable = true ; 
		div_btn.btn_delete.enable = true ; 
	} 
	// 결재선 지정 가능
	if((length(ds_zcst630.GetColumn(0, "APSTAT")) == 0 && ds_zcst630.GetColumn(0, "CS630_PST") == "A1"  && fa_popgbn == "D") || (ds_zcst630.GetColumn(0,"APSTAT") == "3" && fa_popgbn == "B") ){
		div_btn.btn_draft.enable = true ; 
	} 
	//반송 가능
	if((ds_zcst630.GetColumn(0,"APSTAT") == "1" || ds_zcst630.GetColumn(0,"APSTAT") == "4") 
		&& ds_list_0.GetColumn(ds_list_0.FindRow("APNUM","H"+G_USER_ID),"SEQSTAT") == "1") {
		div_btn.btn_sendback.enable = true ; 
	}
	//기안결재완료
	if(ds_zcst630.GetColumn(0,"APSTAT") == "3" && (fa_popgbn == "B" ||(fa_docno > "" && fa_popgbn == "" )) ) { 
		div_btn.btn_draft.enable = true ; 
	}
	//결재수신결재자
	if(ds_zcst630.GetColumn(0,"APSTAT") == "1" && ds_list_0.GetColumn(ds_list_0.FindRow("APNUM","H"+G_USER_ID),"SEQSTAT") == "1" ) {
		div_btn.btn_decide.enable = true ; 
	}
	//부서수신결재자
	if(ds_zcst630.GetColumn(0,"APSTAT") == "4" && ds_list_0.GetColumn(ds_list_0.FindRow("APNUM","H"+G_USER_ID),"SEQSTAT") == "1" ) {
		div_btn.btn_decide.enable = true ; 
	}
	//반송건에 대한 처리 삭제 혹은 재작성
	//if((ds_zcst630.GetColumn(0,"APSTAT") == "2" || ds_zcst630.GetColumn(0,"APSTAT") == "5") && fa_popgbn == "D" ) {
	if((ds_zcst630.GetColumn(0,"APSTAT") == "2" || ds_zcst630.GetColumn(0,"APSTAT") == "5")) {
		div_btn.btn_delete.enable = true ; 
		div_btn.btn_rewrite.enable = true ; 
	}

//	 trace("fa_popgbn = " +fa_popgbn );
//	 trace("APSTAT = "+ds_zcst630.GetColumn(0,"APSTAT")  );
//	 trace("APIRUM = "+ds_list_0.GetColumn(0,"APIRUM"));
//	 trace("CS630_PST = "+ds_zcst630.GetColumn(0, "CS630_PST"));
//	 trace("fa_docno = "+fa_docno);

	fn_popMsg();

	vVkbur = gfn_getBsu(ds_list2.GetColumn(0, "CS126_BSU"), "VKBUR");

}
