/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	//최초 작성 AND 기안 올리기전에 수정 가능
	if(length(trim(ds_list2.GetColumn(0, "CS630_PST"))) != 0 && ds_list2.GetColumn(0, "CS630_PST") != "A1") {
		gfn_showAlert("진행상태를 확인하십시오.");
		return false;
	}
	//필수값 체크
	if(gfn_isNull(cal_cdt, true)) {	 // 중도해지일
		gfn_showAlert("CW00001", st_cdt.value);
		return;
	}
	if(gfn_isNull(cb_crs, true)) {	 // 중도해지 사유
		gfn_showAlert("CW00001", st_crs.value);
		return;
	}
	if(gfn_isNull(ed_dxt, true)) {	 // 당사요구안
		gfn_showAlert("CW00001", st_dxt.value);
		return;
	}
	if(gfn_isNull(ed_gxt, true)) {	 // 고객요구안
		gfn_showAlert("CW00001", st_gxt.value);
		return;
	}
	if(gfn_isNull(ed_hxt, true)) {	 // 협의결과
		gfn_showAlert("CW00001", st_hxt.value);
		return;
	}
	if(gfn_isNull(cal_rpd, true)) {	 // 복구예정일 
		gfn_showAlert("CW00001", st_rpd.value);
		return;
	}
	if(gfn_isNull(ed_rpl, true)) {	 // 복구방안
		gfn_showAlert("CW00001", st_rpl.value);
		return;
	}

	//----------------------------------------------------------------------
	// 중도해지일자 유상계약개시일 보다 크거나 같다
	// 중도해지일자 유상계약만료일 보다 작다
	if(cal_cdt.Value < ds_list2.GetColumn(0, "CS126_USD")) {	 		
		gfn_showAlert("개시일자를 확인하십시오.");
		return;
	}
	if(cal_cdt.Value >= ds_list2.GetColumn(0, "CS126_UMR")) {	 		
		gfn_showAlert("만료일자를 확인하십시오.");
		return;
	}
	//복구예정일자 유상계약개시일 보다 크거나 같다
	if(cal_rpd.Value < cal_cdt.Value) {	 
		gfn_showAlert("복구예정일자는 중도해지일자 보다 이전으로 입력이 불가능합니다.");
		return;
	}
	if(length(ds_list2.GetColumn(0,"CS630_SEQ")) == 0) { // 최초작성
		ds_zcst630.SetColumn(0, "FLAG", "I");
	} else{	                                            // 수정
		ds_zcst630.SetColumn(0, "FLAG", "U");
	}	
	ds_zcst630.SetColumn(0, "CS630_UPN", ds_list2.GetColumn(0,"CS126_UPN"));
	ds_zcst630.SetColumn(0, "CS630_CST", ds_list2.GetColumn(0,"CS126_CST"));
	ds_zcst630.SetColumn(0, "CS630_GNO", ds_list2.GetColumn(0,"CS126_GNO"));
	ds_zcst630.SetColumn(0, "CS630_JSQ", ds_list2.GetColumn(0,"CS126_SEQ"));
	ds_zcst630.SetColumn(0, "CS630_USD", ds_list2.GetColumn(0,"CS126_USD"));
	ds_zcst630.SetColumn(0, "CS630_UMR", ds_list2.GetColumn(0,"CS126_UMR"));
	ds_zcst630.SetColumn(0, "TEM",       ds_list2.GetColumn(0,"VKGRP"));
	ds_zcst630.SetColumn(0, "CS630_SSQ", maxseq); // 인원상주 계약 순번

	fn_chkCancel("A1");
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_chkCancel(pst) {
	ds_list.ClearData();

	for(var i=0; i<ds_list3.RowCount(); i++) {
		ds_list.AddRow();
		ds_list.SetColumn(i, "FLAG", "U");
		ds_list.SetColumn(i, "UPN", ds_list3.GetColumn(i,"CS126_UPN"));
		ds_list.SetColumn(i, "CST", ds_list3.GetColumn(i,"CS126_CST"));
		ds_list.SetColumn(i, "PJT", ds_list3.GetColumn(i,"CS126_PJT"));
		ds_list.SetColumn(i, "HNO", ds_list3.GetColumn(i,"CS126_HNO"));
		ds_list.SetColumn(i, "SEQ", ds_list3.GetColumn(i,"CS126_SEQ"));
		ds_list.SetColumn(i, "USD", ds_list3.GetColumn(i,"CS126_USD"));
		ds_list.SetColumn(i, "UHJ", ds_list3.GetColumn(i,"CS126_UMR"));
		ds_list.SetColumn(i, "GNO", ds_list3.GetColumn(i,"CS126_GNO"));
		ds_list.SetColumn(i, "BSU", ds_list3.GetColumn(i,"CS126_BSU"));
		ds_list.SetColumn(i, "GND", ds_list3.GetColumn(i,"CS126_GND"));
		ds_list.SetColumn(i, "PST", pst);
		ds_list.SetColumn(i, "CRS", ds_zcst630.GetColumn(0,"CS630_CRS"));
		ds_list.SetColumn(i, "CDT", ds_zcst630.GetColumn(0,"CS630_CDT"));

		//================인원상주 유상계약번호별 Max 시퀀스 세팅 시작======================
		//================2019.08.08 인원상주 기존 로직 오류로 인한 수정====================
		//ds_list.SetColumn(i, "MAXSEQ", ds_zcst630.GetColumn(0,"CS630_SSQ"));
		ds_list.SetColumn(i, "MAXSEQ", fn_getCst127SSQ(ds_list3.GetColumn(i,"CS126_UPN"), ds_list3.GetColumn(i,"CS126_CST"), ds_list3.GetColumn(i,"CS126_USD"), ds_list3.GetColumn(i,"CS126_UMR")));
		//================인원상주 유상계약번호별 Max 시퀀스 세팅 종료======================

		if(ds_list3.GetColumn(i,"CS126_GNO") == ds_zcst630.GetColumn(0,"CS630_GNO") ){
			ds_list.SetColumn(i, "UMR", ds_zcst630.GetColumn(0,"CS630_CDT"));			
			// 승인 취소 처리 케이스
			// 해지일자가 계약개시와 같은 경우
			// 해지일자가 유상개시일자 이전인 경우
			//==========================유상계약 기간은 계약개시일이 아닌 유상개시일로 처리해야 한다.(최인식 차장,2019.11.15)
			//if(ds_list3.GetColumn(i,"CS126_USD") == ds_zcst630.GetColumn(0,"CS630_CDT")) {//계약삭제의 경우
				// ds_list.SetColumn(i, "UMS", 0);
			// } else {
				// ds_list.SetColumn(i, "UMS", gfn_getMonthCnt(ds_list3.GetColumn(i, "CS126_UGS"),ds_zcst630.GetColumn(0,"CS630_CDT")));
			// }
			if(ds_list3.GetColumn(i,"CS126_UGS") == ds_zcst630.GetColumn(0,"CS630_CDT")) {//계약삭제의 경우
				ds_list.SetColumn(i, "UMS", 0);
			} else {
				ds_list.SetColumn(i, "UMS", gfn_getMonthCnt(ds_list3.GetColumn(i, "CS126_UGS"),ds_zcst630.GetColumn(0,"CS630_CDT")));
			}
			//==============================================================================================================

			if(substr(ds_list3.GetColumn(i,"CS126_ADT"),0,4) >= "2017" and ds_list3.GetColumn(i,"CS126_UGS") >= ds_zcst630.GetColumn(0,"CS630_CDT")) {//계약삭제의 경우

				ds_list.SetColumn(i, "UMS", 0);
			}
		} else { // 미래계약 삭제
			//=============== 계약개시일이 아닌 유상개시일로 처리해야 한다.(최인식 차장,2019.11.15) ======
			//ds_list.SetColumn(i, "UMR", ds_list3.GetColumn(i,"CS126_USD"));
			ds_list.SetColumn(i, "UMR", ds_list3.GetColumn(i,"CS126_UGS"));
			//============================================================================================
			ds_list.SetColumn(i, "UMS", 0);
		}
	}

	ds_zcst630.SetColumn(0, "CS630_PST", pst);

	// 중도해지 시 매출계획변경 기안 체크(기안반송, 수신반송, 수신결재완료 상태가 아닌경우 리턴처리)
	// 2019.03.05 Han J.H

	if(pst == "A6") {
		fn_salesChangeExist();
	}

	if(chkSalesChangeFlag) {
		return;
	}

	var vMethodName = "";
	if (vVkbur == "H100")
		vMethodName = "cancelJ";
	else 
	    vMethodName = "cancel";

	tit_callService(
        "CS1201001B_ACT"
        , vMethodName
        , "ds_list=ds_list ds_zcst630=ds_zcst630"
        , ""
        , ""
        , "fn_afterCancel");

}

/********************************************************************************
* 기      능   : 유상계약별 인원상주 시퀀스 번호 구하기 
********************************************************************************/
function fn_getCst127SSQ(upn, cst, usd, uhj) {
	ds_cond_person.ClearData();

	ds_cond_person.AddRow();

	if(upn == '' || cst == '' || usd == '' || uhj == '') {
		return;
	}	

	ds_cond_person.SetColumn(0,"UPN",upn);
	ds_cond_person.SetColumn(0,"CST",cst);
	ds_cond_person.SetColumn(0,"USD",usd);
	ds_cond_person.SetColumn(0,"UHJ",uhj);

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs12:CS1201001B_S3", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond_person"
        , "ds_per_val=ds_list"
        , ""
        , ""
        , true
        , false
        , true);

    var mSeq = "";
    if(length(ds_per_val.GetColumn(0,"MAXSEQ")) > 0) {
		mSeq = ds_per_val.GetColumn(0,"MAXSEQ");
    }
    return mSeq;
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_afterCancel(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showConfirm("CE00001"); //처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.
	} else {	
		// 전산 확인용의 경우 
		if(AdminFlag) {
			fn_query();
		} else {
			if (ds_zcst630.GetColumn(0, "FLAG") == "I") {
				fm_makeDoc(); 	     // 최초 저장시 결재문서번호 생성 호출
			} else if(ds_zcst630.GetColumn(0, "FLAG") == "U") {
				gfn_showConfirm("CI00003");
				fn_query(); 		 // 기안 전 수정 시 쿼리 호출
			} else {
				if (vVkbur == "H100"){
					div_btn.btn_save.enable = false ; //저장
					div_btn.btn_draft.enable = false ; //기안
					div_btn.btn_decide.enable = false ; //결재
					div_btn.btn_sendback.Enable = false ; //반송
					div_btn.btn_delete.enable = false ;   //삭제 
					div_btn.btn_rewrite.enable = false ;  //재작성					
				} else {
					fn_decideApproval(); // 결재 처리
				}
			}
		}

	}
}

