/********************************************************************************
* 기      능   : 결재선 지정 pop up

*  'WBAP_DOCKIND'	6	'전자결재 문서구분'	'정기보수중도해지'
********************************************************************************/
function fn_draft(obj) {

	if(length(ed_doc2.Text) == 0) {
		alert("문서번호가 없습니다. 기안작업이 불가능합니다. 정보기술부에 문의주세요.");
		return false;
	}

	if (vVkbur == "H100"){
		ds_wbap01.ClearData();
		ds_wbap02.ClearData();
		ds_wbap01.AddRow();
		ds_wbap01.SetColumn(0, "DOCNO"   , ds_zcst630.GetColumn(0,"CS630_DOC"));
		ds_wbap01.SetColumn(0, "DOCKIND" , "6");

		ds_wbap02.AddRow();
		ds_wbap02.SetColumn(0, "DOCNO"   , ds_zcst630.GetColumn(0,"CS630_DOC"));
		ds_wbap02.SetColumn(0, "DOCKIND" , "6");

		// 2021.01.05 자회사 중도해지 결재 시 중복체크. Han JH
		fn_isDocYnChk();

		if(chk_isDocYn) {        // 동일 문서번호 존재 시 상신처리 하지 않음
			chk_isDocYn = false; // 동일 문서번호 체크 Flag 초기화
			return;
		}

		// 결재정보 공통함수
		gfn_setZwbap(ds_wbap01, ds_wbap02, true);
		// 중도해지 결재처리
		fn_chkCancel("A6");
	} else {
		var appgubn = "S"; //내부 부서 결재

		if(ds_zcst630.GetColumn(0,"APSTAT") == "3") appgubn = "R"; // 수신부서 결재

		var arg = "fa_docno=" + quote(ds_zcst630.GetColumn(0,"CS630_DOC")) + " fa_dockind = " + quote(6) + " fa_appgubn=" + quote(appgubn);

		var returnFlag = gfn_openPopup2("CS0401001A", true, arg);

		if(returnFlag[0] == "S"){
			div_btn.btn_draft.enable = false ;
			fn_query();
		}

		// if(returnFlag[1] == "S"){
			// div_btn.btn_draft.enable = false ;
			// fn_query();
		// }
	}
}

