/********************************************************************************
* 기      능   : 반송 건 재작성
********************************************************************************/
function fn_rewrite() {

	if(!gfn_showConfirm("내용을 수정하여 재작성 하시겠습니까?")) return;

	for(var i=0; i<ds_list2.RowCount(); i++) {
		ds_list2.SetColumn(i, "CS630_DOC" , "");
		ds_list2.SetColumn(i, "CS630_SEQ" , "");
		ds_list2.SetColumn(i, "APSTAT_NM" , "");
		ds_list2.SetColumn(i, "CS630_PST" , "");
	}

	cb_crs.Enable = true;
	cal_cdt.Enable = true;
	ed_dxt.Enable = true;
	ed_gxt.Enable = true;
	ed_hxt.Enable = true;
	cal_rpd.Enable = true;
	ed_rpl.Enable = true;
	cal_mdt.Value = TODAY();
	div_btn.btn_save.enable = true ; //저장
	div_btn.btn_delete.enable = false ;   //삭제 
	div_btn.btn_rewrite.enable = false ;  //재작성

}
function btn_wf_stat_OnClick(obj)
{
	var docNo;
	docNo = ds_list2.GetColumn(0, "CS630_DOC");
	ds_cond.SetColumn(0, "DOCNO", docNo);

	tit_clearActionInfo();														
	tit_addSingleActionInfo("cs01:CS0101008A_S3");								//결재상세정보를 가져온다.
	tit_callService("", "", "ds_cond=ds_cond", "ds_wf_stat=ds_wf_stat", "", "", true, false,true);

	if(ds_wf_stat.rowcount <= 0)
	{
		return;
	}
	var statStr = "";

	for(var iCnt = ds_wf_stat.rowcount - 1; iCnt >= 0; iCnt--)
	{
		statStr += ds_wf_stat.GetColumn(iCnt, "APPGUBNNM") + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQNONM")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "SEQSTATNM")  + "\n    " + 
				   ds_wf_stat.GetColumn(iCnt, "APDPT1_N")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APRNK")  + " / " + 
				   ds_wf_stat.GetColumn(iCnt, "APIRUM")  + "\n";				   	
	}

	gfn_openMsgWin(statStr, "", "A", docNo + " 결재상태");
}

function fn_popMsg() {
	/*
	HRTS 또는 비상통화 장치 임대계약 포함 현장 
    해약 보고 시 위약금에 대한 안내 POP-UP
	*/

	var t_seq = length(ed_doc.Text); // 최초 작성 여부 체크
	var t_docno = length(ed_doc2.Text); // 기안 전 상태 체크
	var apstat = length(ds_list2.GetColumn(0,"APSTAT")); // 기안 전 상태 체크

	if( t_seq == 0 or t_docno == 0 or apstat == 0){
		 // 최초 작성 시 
	} else {
		return;
	}

	var f_fm = "";
	var f_dy = "";
	var f_hy = "";
	for(var i=0; i<ds_list2.GetRowCount(); i++) {
		if(length(ds_list2.GetColumn(i,"CS126_KND_NM")) > 0) { 
			f_fm = "Y";
		}
		if(ds_list2.GetColumn(i,"CS126_DMT") > 0) { 
			f_dy = "Y";
		}
		if(ds_list2.GetColumn(i,"CS126_DMT") > 0) { 
			f_hy = "Y";
		}
	}

	if( f_dy == "Y" or f_hy == "Y" ) {
		var arr = " f_fm=" + quote(f_fm)
		        + " gbn=CS1201001H";
		gfn_openPopup("CS1305001A", true, arr);	
	}

}
