/********************************************************************************
* 기      능   : 중도해지 최종결재 시 매출계획변경 기안 체크로직
                 기안반송, 수신반송, 수신결재완료가 아닌 경우 중도해지가 수신결재처리 되지 않도록 처리. 2019.03.05 Han J.H
********************************************************************************/
function fn_salesChangeExist() {
	ds_halfcond.ClearData();
	ds_halfcond.AddRow();

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs12:CS1201001H_S8");

	if(ds_list2.GetColumn(0,"CS126_UPN") == '' || ds_list2.GetColumn(0,"CS126_CST") == '' || ds_list2.GetColumn(0,"CS126_GNO") == '') {
		return;
	}

	if(ds_list2.GetColumn(0,"CS126_UPN") != '') {
		ds_halfcond.SetColumn(0, "UPN", ds_list2.GetColumn(0,"CS126_UPN"));
	}

	if(ds_list2.GetColumn(0,"CS126_CST") != '') {
		ds_halfcond.SetColumn(0, "CST", ds_list2.GetColumn(0,"CS126_CST"));
	}

	if(ds_list2.GetColumn(0,"CS126_GNO") != '') {
		ds_halfcond.SetColumn(0, "GNO", ds_list2.GetColumn(0,"CS126_GNO"));
	}

	tit_callService(
		""
		, ""
		, "ds_halfcond=ds_halfcond"
		, "ds_sales_exist=ds_sales_exist"
		, ""
		, "salesChangeExist_afterQuery"
        , true
        , false
        , true);	
}

/********************************************************************************
* 기      능   : 중도해지 시 매출변경 확인 Callback
********************************************************************************/
function salesChangeExist_afterQuery(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} else {
		if(ds_sales_exist.GetRowCount() > 0 && !(ds_sales_exist.GetColumn(0,"DOCNO") == null)) {

			if(!(ds_sales_exist.GetColumn(0,"APSTAT") == '2' || ds_sales_exist.GetColumn(0,"APSTAT") == '5' || ds_sales_exist.GetColumn(0,"APSTAT") == '6')) { 
				// 기안반송(2), 수신반송(5), 수신결재완료(6)가 아니면 리턴시킴.
				gfn_showAlert("현재 매출계획변경 진행중인 내역이 있어 중도해지 처리를 진행 할 수 없습니다.\n반송처리 후 재진행 부탁드립니다.\n문서번호 : " + ds_sales_exist.GetColumn(0,"DOCNO"));
				chkSalesChangeFlag = true;
			}
		}	
	}
}

/********************************************************************************
* 기      능   : 동일 문서번호 체크
********************************************************************************/
function fn_isDocYnChk() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs04:CS0401001A_S8");

	ds_isyn_cond.ClearData();
	ds_isyn_cond.AddRow();

	ds_isyn_cond.SetColumn(0, "DOCNO", ds_wbap01.GetColumn(0,"DOCNO"));
	ds_isyn_cond.SetColumn(0, "DOCKIND", ds_wbap01.GetColumn(0,"DOCKIND"));

	// DOCNO 또는 DOCKIND 없을 시 비즈니스 로직 타지 않도록 예외처리
	if(length(ds_isyn_cond.GetColumn(0, "DOCNO")) < 1 || length(ds_isyn_cond.GetColumn(0, "DOCKIND")) < 1) {
		return;
	}

	tit_callService(
	""
	, ""
	, "ds_isyn_cond=ds_isyn_cond"
	, "ds_isyn_result=ds_isyn_result"
	, ""
	, "isDocYnChk_afterQuery"
	, true
	, false
	, true
	);

}

/********************************************************************************
* 기      능   : 동일 문서번호 체크 Callback
********************************************************************************/
function isDocYnChk_afterQuery(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		if(ds_isyn_result.GetRowCount() > 0 && ds_isyn_result.GetColumn(0,"CNT") > 0 ) {
			gfn_showAlert("기안하고자 하는 동일 문서번호가 존재합니다.\n해당 결제창을 종료하시고 다시 결재바랍니다.\n문서번호 : " + ds_isyn_result.GetColumn(0,"DOCNO"));
			chk_isDocYn = true;
		}
	}
}
