#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-3,10);
	cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_year.SetFocus();
	tit_clearActionInfo();

	tit_addSearchActionInfo("cs41:CS4102001A_S2");
	tit_addSearchActionInfo("cs41:CS4102001A_S3");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_area_cd=ds_area_cd ds_lifnr_cd=ds_lifnr_cd"
		, ""
		, "fn_afterQueryCombo"
		, true);
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
	cb_year.index = eNum;
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	//년도필드 필수체크
	if(gfn_isNull(cb_year, true, st_yymm.value))
		return;
	//월필드 필수체크
	if(gfn_isNull(cb_month, true, st_yymm.value))
		return;
	//구분1필드 필수체크
	if(gfn_isNull(cb_gbn1, true, st_gbn.value))
		return;
	//구분2필드 필수체크
	if(gfn_isNull(cb_gbn2, true, st_gbn.value))
		return;

	var Year  = parseInt(ds_cond.GetColumn(0,"YEAR"));
	var Month = parseInt(ds_cond.GetColumn(0,"MONTH"));

	ds_cond.SetColumn(0,"YYMM1",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));
	ds_cond.SetColumn(0,"YYMM2",gfn_getBeforeMonth(ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"),1));
	ds_cond.SetColumn(0,"ARA",cb_area.value);
	ds_cond.SetColumn(0,"BSU",cb_lifnr.value);
	ds_cond.SetColumn(0,"GBU",ds_lifnr_cd.GetColumn(cb_lifnr.Index,"BSU_GB"));
	ds_cond.SetColumn(0,"GBN1",cb_gbn1.value);
	ds_cond.SetColumn(0,"GBN2",cb_gbn2.value);

	var sql = "";
	if(cb_gbn2.value == "01") { // 무상만료
		sql = "cs12:CS1203001B_S1";
	} else if(cb_gbn2.value == "13") { // 무상OnHand
		sql = "cs12:CS1203001B_S13";
	} else if(cb_gbn2.value == "02") { // 신규계약
		sql = "cs12:CS1203001B_S2";
	} else if(cb_gbn2.value == "03") { // 중지
		sql = "cs12:CS1203001B_S3";
	} else if(cb_gbn2.value == "04") { // 재계약대상
		sql = "cs12:CS1203001B_S4";
	} else if(cb_gbn2.value == "05") { // 재계약
		sql = "cs12:CS1203001B_S5";
	} else if(cb_gbn2.value == "14") { // 이관
		sql = "cs12:CS1203001B_S14";
	} else if(cb_gbn2.value == "06") { // 회수
		sql = "cs12:CS1203001B_S6";
	} else if(cb_gbn2.value == "07") { // 실패
		sql = "cs12:CS1203001B_S7";
	} else if(cb_gbn2.value == "08") { // 인수
		sql = "cs12:CS1203001B_S8";
	} else if(cb_gbn2.value == "09") { // 유상
		sql = "cs12:CS1203001B_S9";
	} else if(cb_gbn2.value == "10") { // 무상
		sql = "cs12:CS1203001B_S10";
	} else if(cb_gbn2.value == "11") { // 인계
		sql = "cs12:CS1203001B_S11";
	} else if(cb_gbn2.value == "12") { // 미입주
		sql = "cs12:CS1203001B_S12";
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(sql);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	var user_grp = gfn_getUserGroup();

	ds_gbn2_cd.Filter("GBN == " + quote(""));
	cb_gbn2.Index = 0;
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
