function fn_close() {
	close();
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 0) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

function cb_gbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cb_gbn1.value <> "") {
		ds_gbn2_cd.Filter("GBN == " + quote(strCode));
		cb_gbn2.Index = 0;
	} else {
		ds_gbn2_cd.Filter("GBN == " + quote(""));
		cb_gbn2.Index = 0;
	}
}

function fn_afterQueryCombo(errCode, errMsg) {

	if(gfn_getUserGroup() == "10")
	{
		cb_area.Enable = false;
		cb_lifnr.Index = 0;
	}
	else if(gfn_getUserGroup() == "20" || gfn_getUserGroup() == "30")
	{
		cb_area.Enable = true;
		ds_area_cd.filter("CODE == " + quote(G_AREA_CODE));
		cb_area.Index = 0;
		// 협력사 전체추가
		gfn_insertEmptyRow(ds_lifnr_cd, G_CODE_ALL);
		cb_lifnr.value = "";
		ds_lifnr_cd.filter("BSU_ARA == " + quote(G_AREA_CODE) +" || CODE == ''");
	}
	else if(gfn_getUserGroup() == "40")
	{
		// 지역 전체추가
		gfn_insertEmptyRow(ds_area_cd, G_CODE_ALL);
		cb_area.value = "";
		// 협력사 전체추가
		gfn_insertEmptyRow(ds_lifnr_cd, G_CODE_ALL);
		cb_lifnr.value = "";
	}
}

/********************************************************************************
* 기      능   : 지역콤보 변경시 보수협력사 콤보 filtering해줌
********************************************************************************/
function cb_area_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cb_lifnr.value = "";
	ds_lifnr_cd.filter("BSU_ARA == " + quote(strCode) +" || CODE == ''" );
}
