/********************************************************************************
* 기      능   : 저장 (excel 파일 저장)
********************************************************************************/
function fn_save() {
//	alert("save");

	ds_uplist.ClearData();
	gfn_copyDs(ds_list, ds_uplist, "CHECK=1");

	if (ds_uplist.rowcount() <1) {
		alert("수정/입력 항목이 없습니다.");
		return;
	}

	alert(ds_uplist.rowcount());
	TRACE(ds_uplist.SaveXML());
	tit_clearActionInfo();		
	tit_addMultiActionInfo("cs12:CS1208001A_U1");
	tit_callService(
	""
	, ""
	, "ds_uplist=ds_uplist"
	, ""
	, ""
	, "fn_afterUpdate");

}

