function fn_afterqueryCombo2( errCode, errMsg ) 
{
	gfn_insertEmptyRow(_ds_mid_cd, G_CODE_ALL);

	//
	// 사용자 지정을 통한 버튼 권한 관리 부분
	// 조영구 과장 : 2022095
	// 최인식 과장 : 2017938
	// 송준호 과장 : 2025787
	if (G_USER_ID =='2022095' OR G_USER_ID =='TEST_LKJ' OR G_USER_ID =='2021200' OR G_USER_ID =='2018900' OR G_USER_ID =='2029758' OR G_USER_ID == "2029758"
	    OR G_USER_ID =='2017938' OR G_USER_ID ='2023687' OR G_USER_ID ='2017750'
	    OR G_USER_ID == '2025787'
	    OR G_USER_ID == '2017911'	//민준홍 과장 추가
	    OR G_USER_ID == '2020041'	//조영재 DL 추가
	    OR G_USER_ID == '2025388'   //김나영
	    OR G_USER_ID == '2019523'  //윤아로 SW 추가. 2019.02.15 요청
	    OR G_USER_ID == '2019086' OR G_USER_ID == '2022834' //양부원씨 요청으로 2020.03.16	추가
	    )  

	{
		btn_download.Visible=true;

		cb_mid.Index = 0;
		cb_mid.Enable = true;
	} else {
		cb_mid.Value = G_VEND_NAME;
		cb_mid.Enable = false;

		ufn_addCode( _ds_small_cd, G_SAP_USER_VGCD, G_VEND_NAME);

		cb_small.Index = 1;
		cb_small.Visible = false;

		btn_save.Enable = false;
		grd_list.Editable = false;

	}
}

function cb_mid_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if ( strCode == "" ) 
	{
		_ds_small_cd.ClearData();
		gfn_insertEmptyRow(_ds_small_cd, G_CODE_ALL);
		cb_small.index = 0;
		cb_small.Enable=false;
		return;
	}  

	ufn_queryCombo4("VKGRP");	// 팀구분 조회
}

