/********************************************************************************
* 기      능   : dataset에 코드 추가 
********************************************************************************/
function ufn_addCode( dsObj, code, codeName) 
{
	var nRow = dsObj.addRow();

	dsObj.setColumn(nRow, "CODE", code);
	dsObj.setColumn(nRow, "CODE_NAME", codeName);

	return nRow;
}

