var vBtn_auth	= false;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	var codeArr = ["CS158", "CS179"];
    var dsObjArr = [ds_reason_cd, ds_hep];
    var addTypeArr = [G_CODE_NONE, G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    var t_date = gfn_getAfterMonth(substr(gfn_getCurrDate(),0,6), 0);
	var curYear = substr(t_date,0,4);
	fn_makeYear(curYear,-5,5);
//	cb_month.index = parseInt(substr(t_date,4,2))-1;
//	cb_year.SetFocus();

	cb_detail.Index = 0;

	//  최근 저장한 이동계획 작성 기준일자 가져오기
	fn_getAym();

	//================== 대상집계, 원가계산, 작업년월 버튼 권한 ===========
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1213001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vBtn_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	//// 대상 집계 권한 - 최인식 이경해 
	//if(G_USER_ID == '2017938' || G_USER_ID == '2023466' || G_USER_ID == '2021200' || 
	//   G_USER_ID == '2023687' || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") {
	if (vBtn_auth == true){
	//==========================================================
		Button2.Visible = true; 
		btn_cost.Visible = true;
		cb_month.Enable = true;
		cb_year.Enable = true;
	} else {
		Button2.Visible = false;
		btn_cost.Visible = false;
		cb_month.Enable = false;
		cb_year.Enable = false;
	}
	var user_grp = gfn_getUserGroup();
	if( user_grp == '40' )
		btn_cost_search.Visible = true;
	else
		btn_cost_search.Visible = false;
}

/********************************************************************************
* 기      능   : 최근 저장한 이동계획 작성 기준일자 가져오기
********************************************************************************/
function fn_getAym() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1213002A_S3");
	tit_callService(
        ""
        , ""
        , ""
        , "ds_aym=ds_aym"
        , ""
        , "fn_afterGetAym"
        , true);
}
function fn_afterGetAym() {
	//일자 셋팅 
	cal_dat.value = ds_aym.GetColumn(0, "CS620_DAT");
	cal_sta.Value = gfn_getBeforeMonth(cal_dat.value, 2)+'01';
	cb_year.Value = substr(cal_sta.Value,0,4);
	cb_month.Value = substr(cal_sta.Value,4,2);
	cal_end.Value = gfn_getLeapLastDay(gfn_getAfterMonth(cb_year.value + cb_month.value, 2) + "01");
}
/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum)
{
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--)
	{
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}
//	cb_year.index = eNum;
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	/* CS1229001A 연결 */
	if(length(trim(s_cod)) > 0) {
		if(s_cod == "000") {
			s_cod = "X";
		}

		ds_cond2.SetColumn(0, "ARA", s_ara);
		ds_cond2.SetColumn(0, "BSU", s_bsu);
		ds_cond2.SetColumn(0, "GBN", s_gbn);
		ds_cond2.SetColumn(0, "COD", s_cod);
		ds_cond2.SetColumn(0, "DAT", s_dat);
		ds_cond2.SetColumn(0, "DAT1", gfn_getLeapLastDay(gfn_getBeforeMonth(substr(s_dat,0,6), 1) + "01"));
		ds_cond2.SetColumn(0, "HST", s_hst);

		fn_query2();
	}
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	if(gfn_isNull(cal_dat, true, st_dat.value)) {
		return;
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else if(div_code.cb_big.value == '4') { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "DPT", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BSU", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	ds_cond.SetColumn(0, "PJT", ed_pjt.Value);
	ds_cond.SetColumn(0, "PRO", cb_detail.Value);
	ds_cond.SetColumn(0, "DAT", cal_dat.Value);
	ds_cond.SetColumn(0, "DAT1", gfn_getLeapLastDay(gfn_getBeforeMonth(substr(cal_dat.Value,0,6), 1) + "01"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1213001A_S6");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

function fn_query2() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1213001A_S3");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_list2.ClearData();
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
	var strColID = grd_list.GetCellProp("head", 0, "Text");
	if ( length( strColID ) == 1 ) {
		grd_list.SetCellProp("head", 0, "Text", "0");
	}
}

