/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	ds_list2.ClearData();

	var j = 0;
	ds_list.FireEvent = false;

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우

			//실패(교체)예정일 입력시에는 필수 입력값 체크 제외한다. 201502 이하림
			if(length(trim(ds_list.GetColumn(i, "FDT"))) == 0) {

				if(length(trim(ds_list.GetColumn(i, "YDT"))) == 0) {
					gfn_showAlert(i + 1 + "번째 호기의 계약예정일을 입력하시기 바랍니다.");
					return false;
				}

				if(ds_list.GetColumn(i, "YMT") <= 0) {
					gfn_showAlert(i + 1 + "번째 호기의 계약단가를 입력하시기 바랍니다.");
					return false;
				}
				// 호기상태별 만료일-계약예정일 체크 201302 LHR 
				if(ds_list.GetColumn(i, "HST") == "미발주") {
					if(parseInt(trim(ds_list.GetColumn(i, "ISD"))) >= parseInt(trim(ds_list.GetColumn(i, "YDT")))) {
						gfn_showAlert(i + 1 + "번째 호기의 계약예정일이 인수일보다 작습니다.\n다시 입력하시기 바랍니다.");
						return false;
					}			
				} 
				// 최인식 과장 요청 , 체크 해제  20150401 이하림
				// else if(ds_list.GetColumn(i, "HST") == "무상공사만료" || ds_list.GetColumn(i, "HST") == "유상공사만료") {
					// if(gfn_getAfterMonth(substr(ds_list.GetColumn(i, "BHDA"),0,6), parseInt(trim(ds_list.GetColumn(i, "MIM")))) + substr(ds_list.GetColumn(i, "BHDA"),6,2)
						// >= parseInt(trim(ds_list.GetColumn(i, "YDT")))) {
						// gfn_showAlert(i + 1 + "번째 호기의 계약예정일이 무상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
						// return false;
					// }			
				// } else if(ds_list.GetColumn(i, "HST") == "무상일반만료" || ds_list.GetColumn(i, "HST") == "전환펜딩실패") {
					// if(parseInt(trim(ds_list.GetColumn(i, "BHDB"))) >= parseInt(trim(ds_list.GetColumn(i, "YDT")))) {
						// gfn_showAlert(i + 1 + "번째 호기의 계약예정일이 무상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
						// return false;
					// }
				// } else if(ds_list.GetColumn(i, "HST") == "유상일반만료" || ds_list.GetColumn(i, "HST") == "갱신펜딩실패") {
					// if(parseInt(trim(ds_list.GetColumn(i, "UID"))) >= parseInt(trim(ds_list.GetColumn(i, "YDT")))) {
						// gfn_showAlert(i + 1 + "번째 호기의 계약예정일이 유상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
						// return false;
					// }		
				// }
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(j, "UPN2", ds_list.GetColumn(i, "UPN"));
			ds_list2.SetColumn(j, "PJT2", ds_list.GetColumn(i, "PJT"));
			ds_list2.SetColumn(j, "RSN2", ds_list.GetColumn(i, "RSN"));		//사유
			ds_list2.SetColumn(j, "RMK2", ds_list.GetColumn(i, "RMK"));		//비고
			ds_list2.SetColumn(j, "UHJ2", ds_list.GetColumn(i, "UID"));		//유상일반만료일
			ds_list2.SetColumn(j, "STS2", ds_list.GetColumn(i, "HST"));		//호기상태
			ds_list2.SetColumn(j, "BDT2", "");
			ds_list2.SetColumn(j, "YDT2", ds_list.GetColumn(i, "YDT"));		//계약예정일
			ds_list2.SetColumn(j, "FDT2", ds_list.GetColumn(i, "FDT"));		//실패예정일
			ds_list2.SetColumn(j, "PFM2", "");
			ds_list2.SetColumn(j, "YBN2", ds_list.GetColumn(i, "FYN"));		//FM여부
			ds_list2.SetColumn(j, "YMT2", ds_list.GetColumn(i, "YMT"));		//계약단가
			ds_list2.SetColumn(j, "YON2", ds_list.GetColumn(i, "YON"));		//계약개월수

			ds_list2.SetColumn(j, "YDA2", (ds_list.GetColumn(i, "YMT") + ds_list.GetColumn(i, "HMT") + ds_list.GetColumn(i, "DMT")) * iif(ds_list.GetColumn(i, "YON") > 12, 12,ds_list.GetColumn(i, "YON")) );		//수주예상금액
			//ds_list2.SetColumn(j, "PHRTS2", "");
			if(parseInt(ds_list.GetColumn(i, "HMT")) > 0){ 
				ds_list2.SetColumn(j, "HYN2", "1");		//HRTS여부
				ds_list2.SetColumn(j, "HMT2", ds_list.GetColumn(i, "HMT"));		//HRTS료
			} else {
				ds_list2.SetColumn(j, "HYN2", "0");		//HRTS여부
				ds_list2.SetColumn(j, "HMT2", "0");		//HTRS료
			}
			//ds_list2.SetColumn(j, "DYN2", ds_list.GetColumn(i, "DYN"));		//DI-PBX여부
			if(parseInt(ds_list.GetColumn(i, "DMT")) > 0){ 
				ds_list2.SetColumn(j, "DMT2", ds_list.GetColumn(i, "DMT"));		//DI-PBX료
			} else {
				ds_list2.SetColumn(j, "DMT2", 0);		//DI-PBX료
			}

			if(ds_list.GetColumn(i, "HST") == "유상일반만료" || ds_list.GetColumn(i, "HST") == "갱신펜딩실패" ){
				if(gfn_getMonthCnt(ds_list.GetColumn(i, "UID"), ds_list.GetColumn(i, "YDT")) > 2){
					ds_list2.SetColumn(j, "SGB2", "회수(갱신펜딩)");
				} else {
					ds_list2.SetColumn(j, "SGB2", "갱신계약");
				}
			} else if(ds_list.GetColumn(i, "HST") == "무상일반만료" || ds_list.GetColumn(i, "HST") == "전환펜딩실패"){
				if(gfn_getMonthCnt(ds_list.GetColumn(i, "BHDB"), ds_list.GetColumn(i, "YDT")) > 2){
					ds_list2.SetColumn(j, "SGB2", "회수(전환펜딩)");
				} else {
					ds_list2.SetColumn(j, "SGB2", "전환계약");
				}
			} else {
				ds_list2.SetColumn(j, "SGB2", "전환계약");	
			}

			ds_list2.SetColumn(j, "USD", ds_list.GetColumn(i, "USD"));	//유상개시일시
			ds_list2.SetColumn(j, "UMR", ds_list.GetColumn(i, "UMR"));	//유상만료일
			ds_list2.SetColumn(j, "HEP", ds_list.GetColumn(i, "HEP"));	//점검주기

			ds_list2.SetColumn(j, "DAT", ds_list.GetColumn(i, "DAT"));
			ds_list2.SetColumn(j, "SPC2", ds_list.GetColumn(i, "SPC2"));
			ds_list2.SetColumn(j, "SPC", ds_list.GetColumn(i, "SPC"));
			ds_list2.SetColumn(j, "PRO", ds_list.GetColumn(i, "PRO"));
			ds_list2.SetColumn(j, "SPC3", ds_list.GetColumn(i, "SPC3"));
			ds_list2.SetColumn(j, "SPC7", ds_list.GetColumn(i, "SPC7"));
			ds_list2.SetColumn(j, "BSU", ds_list.GetColumn(i, "BSU"));
			ds_list2.SetColumn(j, "NAM", ds_list.GetColumn(i, "NAM")); // 거래선(고객명) 키값으로 추가 20150324 이하림
			ds_list2.SetColumn(j, "FFM", ds_list.GetColumn(i, "FFM"));	//최초FM여부
			ds_list2.SetColumn(j, "FHG", ds_list.GetColumn(i, "FHG"));	//최초HRTS여부

			j++;

		}
	}

	ds_list.FireEvent = true;

	//btn_save.Enable = false;
	tit_clearActionInfo();

	// 미발주 및 만료사유 updaet(zcst113)
	tit_addMultiActionInfo("cs12:CS1213001A_U1");
	tit_addMultiActionInfo("cs12:CS1213001A_I1");

//	trace(ds_list2.SaveXml());	

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list2:A"
        , ""
        , ""
        , "fn_afterSave"
        , true);
}

