function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var rect = obj.GetCellRect(nRow, ncell);

	var nX = ClientToScreenX(obj,rect[0]);
	var nY = ClientToScreenY(obj,rect[1] + 20);

	PopupDiv0.TrackPopup(nX,nY);

	if(nCell == 48) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "YDT");
	} else if(nCell == 49) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "USD");
	} else if(nCell == 50) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "UMR");
	} else if(nCell == 61) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "FDT");
	}

	PopupDiv0.cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우
********************************************************************************/
function PopupDiv0_cal_temp_OnKillFocus(obj) {
	PopupDiv0.ClosePopup();
}

function PopupDiv0_cal_temp_OnDayClick(obj,strText) {
	gfn_clearMsgBar();
	var diff;
	if(grd_list.GetClickedCell() == 48) {	//계약예정일
		ds_list.SetColumn(ds_list.row, "YDT", strText);
		//if(length(ds_list.GetColumn(ds_list.row, "USD")) == 0 )
			ds_list.SetColumn(ds_list.row, "USD", strText);
		//if(length(ds_list.GetColumn(ds_list.row, "UMR")) == 0 ){
			//if(ds_list.GetColumn(ds_list.row, "FYN") == 1 || parseInt(ds_list.GetColumn(ds_list.row, "HMT")) > 0 || parseInt(ds_list.GetColumn(ds_list.row, "DMT")) > 0 )
				//ds_list.SetColumn(ds_list.row, "UMR", AddDate(AddMonth(strText,60),-1));
			//else
				ds_list.SetColumn(ds_list.row, "UMR", AddDate(AddMonth(strText,12),-1));
		//}
		ds_list.SetColumn(ds_list.row, "FDT", "");
		diff = gfn_getMonthCnt(ds_list.GetColumn(ds_list.row, "USD"), ds_list.GetColumn(ds_list.row, "UMR"));
		if(parseInt(diff) < 0 ) diff = 0;
		ds_list.SetColumn(ds_list.row, "YON", diff);
	} else if(grd_list.GetClickedCell() == 49) {	//유상개시일 
		ds_list.SetColumn(ds_list.row, "USD", strText);
		//if(length(ds_list.GetColumn(ds_list.row, "UMR")) == 0 ){
			//if(ds_list.GetColumn(ds_list.row, "FYN") == 1 || parseInt(ds_list.GetColumn(ds_list.row, "HMT")) > 0 || parseInt(ds_list.GetColumn(ds_list.row, "DMT")) > 0 )
				//ds_list.SetColumn(ds_list.row, "UMR", AddMonth(strText,60));
			//else
				ds_list.SetColumn(ds_list.row, "UMR", AddDate(AddMonth(strText,12),-1));
		//}
		ds_list.SetColumn(ds_list.row, "FDT", "");
		//gfn_getMonthCnt(substr(ds_list.GetColumn(ds_list.row, "USD"),0,6)+"01", substr(ds_list.GetColumn(ds_list.row, "UMR"),0,6)+"01");
		diff = gfn_getMonthCnt(ds_list.GetColumn(ds_list.row, "USD"), ds_list.GetColumn(ds_list.row, "UMR"));
		if(parseInt(diff) < 0 ) diff = 0;
		ds_list.SetColumn(ds_list.row, "YON", diff);
	} else if(grd_list.GetClickedCell() == 50) {	//유상만료일
		ds_list.SetColumn(ds_list.row, "UMR", strText);
		/*
		if(length(ds_list.GetColumn(ds_list.row, "USD")) == 0 ){
			if(ds_list.GetColumn(ds_list.row, "FYN") == 1 || parseInt(ds_list.GetColumn(ds_list.row, "HMT")) > 0 || parseInt(ds_list.GetColumn(ds_list.row, "DMT")) > 0 )
				ds_list.SetColumn(ds_list.row, "USD", AddMonth(strText,-60));
			else
				ds_list.SetColumn(ds_list.row, "USD", AddMonth(strText,-12));
		}
		*/
		ds_list.SetColumn(ds_list.row, "FDT", "");
		diff = gfn_getMonthCnt(ds_list.GetColumn(ds_list.row, "USD"), ds_list.GetColumn(ds_list.row, "UMR"));
		if(parseInt(diff) < 0 ) diff = 0;
		ds_list.SetColumn(ds_list.row, "YON", diff);
	} else if(grd_list.GetClickedCell() == 61) {	//실패예정일
		ds_list.SetColumn(ds_list.row, "FDT", strText);
		ds_list.SetColumn(ds_list.row, "YDT", "");
		ds_list.SetColumn(ds_list.row, "USD", "");
		ds_list.SetColumn(ds_list.row, "UMR", "");
		ds_list.SetColumn(ds_list.row, "RSN", "");
		ds_list.SetColumn(ds_list.row, "YON", 0);
	}
	PopupDiv0.ClosePopup();
}

