}

// 대상집계버튼클릭 이벤트
function fn_button2_Onclick(obj){

	if(gfn_isNull(cal_dat, true, st_dat.value)) {
		return;
	}

	ds_cond.SetColumn(0, "DAT", cal_dat.Value);
	ds_cond.SetColumn(0, "DAT1", gfn_getLeapLastDay(gfn_getBeforeMonth(substr(cal_dat.Value,0,6), 1) + "01"));
	ds_cond.SetColumn(0, "SDAT", cal_sta.Value); //경과 개월수 계산 , 계획년월의 첫 시작월

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1213001A_S5", null, true);
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_result=ds_result"
        , ""
        , "fn_afterQuery2"
        , true, 
        true);
}

function fn_insert_obj(){
	gfn_clearMsgBar();

	tit_clearActionInfo();
	// 기준일자 데이터 삭제
	tit_addMultiActionInfo("cs12:CS1213001A_D1");
	// 기준일자 데이터 등록
	tit_addMultiActionInfo("cs12:CS1213001A_I2");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterSave2");
}


//대상 데이터 집계 callback
function fn_afterQuery2(errCode, errMsg) {

	var isSave = true;
	if(ds_result.RowCount > 0) isSave = false;

	if(!isSave && confirm('기준일자에 대상이 존재 합니다. 다시 등록하시겠습니까?')){
		isSave = true;
	}

	if(isSave){
		fn_insert_obj();
	}
}

function cb_year_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cal_dat.Value = gfn_getAfterMonth(cb_year.value + cb_month.value, 2)+'01';
	cal_sta.Value = gfn_getAfterMonth(cb_year.value + cb_month.value, 0)+'01';
	cal_end.Value = gfn_getLeapLastDay(gfn_getAfterMonth(cb_year.value + cb_month.value, 2) + "01");
}

function cb_month_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	cal_dat.Value = gfn_getAfterMonth(cb_year.value + cb_month.value, 2)+'01';
	cal_sta.Value = gfn_getAfterMonth(cb_year.value + cb_month.value, 0)+'01';
	cal_end.Value = gfn_getLeapLastDay(gfn_getAfterMonth(cb_year.value + cb_month.value, 2) + "01");
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//ds_list.FireEvent = false;
	if(strColumnID != "CHK") 
		ds_list.SetColumn(nRow, "CHK", "1");

	//ds_list.FireEvent = true;
}

function btn_auto_OnClick(obj)
{
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		return;
	}

	ds_list.FireEvent = false;

	var strText;
	var addMon;
	for(var i=0; i<ds_list.RowCount(); i++) {
		strText = '';
		addMon = 12;
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "PRO") == '1' && ds_list.GetColumn(i, "HST") == '미발주' && ds_list.GetColumn(i, "ISD") != ''){
				strText = AddMonth(ds_list.GetColumn(i, "ISD"), parseInt(ds_list.GetColumn(i, "MGM")) + parseInt(ds_list.GetColumn(i, "MIM")) + 1);
			}else if(ds_list.GetColumn(i, "PRO") == '1' && ds_list.GetColumn(i, "HST") == '무상공사만료' && ds_list.GetColumn(i, "BHDA") != ''){
				strText = AddMonth(ds_list.GetColumn(i, "BHDA"), parseInt(ds_list.GetColumn(i, "MIM")) + 1);
			}else if(ds_list.GetColumn(i, "PRO") == '1' && ds_list.GetColumn(i, "HST") == '유상공사만료' && ds_list.GetColumn(i, "UIC") != ''){
				strText = AddMonth(ds_list.GetColumn(i, "UIC"), parseInt(ds_list.GetColumn(i, "MIM")) + 1);
			}else if(ds_list.GetColumn(i, "PRO") == '2' && ds_list.GetColumn(i, "BHDB") != ''){
				strText = AddMonth(ds_list.GetColumn(i, "BHDB"), 1);
			}else if(ds_list.GetColumn(i, "PRO") == '3' && ds_list.GetColumn(i, "UID") != ''){
				strText = AddDate(ds_list.GetColumn(i, "UID"), 1);
			}
			if(strText < cal_sta.Value) strText = cal_sta.Value;
			ds_list.SetColumn(i, "YDT", strText);
			ds_list.SetColumn(i, "USD", strText);

			ds_list.SetColumn(I, "UMR", AddDate(AddMonth(strText,12),-1));

			if(ds_list.GetColumn(i, "PRO") != '3'){
				ds_list.SetColumn(i, "YMT", ds_list.GetColumn(i, "AMT"));
				ds_list.SetColumn(i, "HEP", "01");
				ds_list.SetColumn(i, "RSN", "");
				ds_list.SetColumn(i, "RMK", "");
			}

			diff = gfn_getMonthCnt(ds_list.GetColumn(I, "USD"), ds_list.GetColumn(I, "UMR"));
			if(parseInt(diff) < 0 ) diff = 0;
			ds_list.SetColumn(i, "YON", diff);
			ds_list.SetColumn(i, "FDT", "");

			if(parseInt(ds_list.GetColumn(i, "PMON")) = 0){
				if(ds_list.GetColumn(i, "PRO") = '1')
					ds_list.SetColumn(i, "RSN", "B05");
				else if(ds_list.GetColumn(i, "PRO") = '2')
					ds_list.SetColumn(i, "RSN", "M08");
				else if(ds_list.GetColumn(i, "PRO") = '3')
					ds_list.SetColumn(i, "RSN", "U07");
			}else{
				ds_list.SetColumn(i, "RSN", "");
			}
		}
	}

	ds_list.FireEvent = true;
}

