function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,5);

	cb_gbn.Index = 2;
	cb_pbn.Index = 0;
}

/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_year1.AddRow();
		ds_year1.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year1.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");

		ds_year2.AddRow();
		ds_year2.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year2.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");

		ds_year3.AddRow();
		ds_year3.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year3.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");

		ds_year4.AddRow();
		ds_year4.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year4.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");

		row++;
	}

	gfn_insertEmptyRow(ds_year1, G_CODE_ALL);
	gfn_insertEmptyRow(ds_year2, G_CODE_ALL);
	gfn_insertEmptyRow(ds_year3, G_CODE_ALL);
	gfn_insertEmptyRow(ds_year4, G_CODE_ALL);

	cb_year1.Index = 0;
	cb_year2.Index = 0;
	/*cb_year3.Index = 0;
	cb_year4.Index = 0;*/

	// 2019.04.12 실패년도 강제세팅. Han J.H
	var curYear = substr(gfn_getCurrDate(),0,4);

	cb_year3.Index = ds_year3.FindRow("CODE",ParseInt(curYear)-1);
	cb_year4.Index = ds_year4.FindRow("CODE",ParseInt(curYear));

//	cb_year1.index = eNum + 1;
//	cb_year2.index = eNum;
//	cb_year3.index = eNum + 1;
//	cb_year4.index = eNum;
}

