}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	if(length(cb_year1.value) > 0 && length(cb_year2.value) == 0) {
		gfn_showAlert("회수예정년도를 입력하십시요.");
		return false;
	}

	if(length(cb_year1.value) == 0 && length(cb_year2.value) > 0) {
		gfn_showAlert("회수예정년도를 입력하십시요.");
		return false;
	}

	if(length(cb_year3.value) > 0 && length(cb_year4.value) == 0) {
		gfn_showAlert("실패년도를 입력하십시요.");
		return false;
	}

	if(length(cb_year3.value) == 0 && length(cb_year4.value) > 0) {
		gfn_showAlert("실패년도를 입력하십시요.");
		return false;
	}

	if(cb_year1.value > cb_year2.value) {
		gfn_showAlert("회수예정년도를 확인하십시요.");
		return false;
	}

	if(cb_year3.value > cb_year4.value) {
		gfn_showAlert("실패년도를 확인하십시요.");
		return false;
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	ds_cond.SetColumn(0, "PJT", ed_pjt.value);
	ds_cond.SetColumn(0, "GBN", cb_gbn.value);
	ds_cond.SetColumn(0, "YEAR1", cb_year1.value);
	ds_cond.SetColumn(0, "YEAR2", cb_year2.value);
	ds_cond.SetColumn(0, "YEAR3", cb_year3.value);
	ds_cond.SetColumn(0, "YEAR4", cb_year4.value);

	// Proj 필수조건이 아니면
	/*if(!length(trim(ds_cond.GetColumn(0,"PJT")))) {
		// 실패년도는 필수값으로 입력되어야 하며, 전체로는 조회불가
		if(cb_year3.Index == 0 || cb_year4.Index == 0) {
			gfn_showAlert("실패년도를 입력하십시요.");
			cb_year3.SetFocus();
			return false;
		}
	}*/	

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1224001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_list2.ClearData();
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

