function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID == "YMT" || strColumnID == "YON" || strColumnID == "HMT") {
		ds_list.FireEvent = false;
		ds_list.SetColumn(nRow, "YDA", parseFloat(ds_list.GetColumn(nRow, "YMT") + ds_list.GetColumn(nRow, "HMT")) * parseFloat(ds_list.GetColumn(nRow, "YON")));
		ds_list.FireEvent = true;
	}
}

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var rect = obj.GetCellRect(nRow, ncell);

	var nX = ClientToScreenX(obj,rect[0]);
	var nY = ClientToScreenY(obj,rect[1] + 20);

	PopupDiv0.TrackPopup(nX,nY);

	if(nCell == 32) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "YDT");
	}

	PopupDiv0.cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우
********************************************************************************/
function PopupDiv0_cal_temp_OnKillFocus(obj) {
	PopupDiv0.ClosePopup();
}

function PopupDiv0_cal_temp_OnDayClick(obj,strText) {
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 32) {
		ds_list.SetColumn(ds_list.row, "YDT", strText);
	}

	PopupDiv0.ClosePopup();
}

function btn_copy_OnClick(obj) {
	if(ds_list.rowCount() < 1) {
		// 삭제할 정보 없음. 
		return;
	}

	var arg = "rows=" + quote(ds_list.lastrow+1);
	gfn_openPopup("CS1224001B", true, arg);
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell == "35") {	//HRTS 여부
		if(ds_list.GetColumn(nRow, "HYN") == "") {
			grd_list.SetCellProp("body",36,"Edit","number");
			return;
		} else {
			grd_list.SetCellProp("body",36,"Edit","none");
			ds_list.SetColumn(nRow, "HMT", 0);
			return;
		}
	}

	if(nCell == "36") {	//HRTS 단가
		if(ds_list.GetColumn(nRow, "HYN") == "1") {
			grd_list.SetCellProp("body",36,"Edit","number");
			return;
		} else {
			grd_list.SetCellProp("body",36,"Edit","none");
			return;
		}
	}
}
