	gfn_initForm(true);

	cal_dat.Value = gfn_getCurrDate();
	cb_gbn.Index = 0;

	Tab00.TabIndex = 0;
	ufn_enableBtn("save" ,false);

	/* 공통코드 조회(검사원) */
	var codeArr = ["CS172"];
    var dsObjArr = [ds_ear_cd];
    var addTypeArr = [G_CODE_SELECT];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

}

