/********************************************************************************
* 기      능   : Calendar 관련 
********************************************************************************/
function PopupDiv0_cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(Tab00.tab2.grd_list.GetClickedCell() == 9) {
		ds_list2.SetColumn(ds_list2.row, "IHD", strText);
	}
	if(Tab00.tab2.grd_list.GetClickedCell() == 13) {
		ds_list2.SetColumn(ds_list2.row, "CRD", strText);
	}
	if(Tab00.tab2.grd_list.GetClickedCell() == 15) {
		ds_list2.SetColumn(ds_list2.row, "IDD", strText);
	}

	PopupDiv0.ClosePopup();
}

function Tab00_tab2_grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var vColId = obj.GetCellProp("BODY",nCell,"colid");

	if (vColId == "OFFICE_NAME") {		//if(nCell == 14) { 
		var arr = "rowidx=" + nRow ;
		gfn_openPopup("CS1228001C", true , arr);

	} else {
		var rect = obj.GetCellRect(nRow, ncell);

		var nX = ClientToScreenX(obj,rect[0]);
		var nY = ClientToScreenY(obj,rect[1] + 20);

		PopupDiv0.TrackPopup(nX,nY);

		if (vColId == "IHD"){			//if(nCell == 9) {
			PopupDiv0.cal_temp.value = ds_list2.getColumn(nRow, "IHD");
		}	

		if (vColId == "CRD"				//if(nCell == 13 
		&& (G_USER_ID == "2021056" || // 20130819 수수료입금일 수정 제한
			G_USER_ID == "2019752" || // 강화영-광주
			G_USER_ID == "5003814" || // 김예진-경남
			G_USER_ID == "2017172" || // 김은지-대구
			G_USER_ID == "2013479" || // 박경희-강원
			G_USER_ID == "2018225" || // 박신혜-제주
			G_USER_ID == "2017130" || // 설정아-전주
			G_USER_ID == "5003822" || // 김솔지-대전
			G_USER_ID == "2020351" || // 유다혜-인천
			G_USER_ID == "5004217" || // 임유진-울산
			G_USER_ID == "5003792" || // 장임희-부산
			G_USER_ID == "2020548" || // 허은경-경기	
			G_USER_ID == "2019876" || // 김소연  서울협력
			G_USER_ID == "5008611"	  // 이가은  분당서비스팀
			)
		) { 
			PopupDiv0.TrackPopup(nX,nY);
			PopupDiv0.cal_temp.value = ds_list2.getColumn(nRow, "CRD");
		}

		if (vColId == "IDD") {	 		//if(nCell == 15) {
			PopupDiv0.cal_temp.value = ds_list2.getColumn(nRow, "IDD");
		}
		PopupDiv0.cal_temp.SetFocus();
	}
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save(){

	gfn_clearMsgBar();

    if(ds_list2.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	ds_list3.ClearData();

	var j = 0;
	for(var i=0; i<ds_list2.RowCount(); i++) {					

		if(ds_list2.GetColumn(i, "CHK") == true) {		

			if(length(trim(ds_list2.GetColumn(i, "IHD"))) != 0){	
				if(ds_list2.GetColumn(i, "IHD") < ds_list2.GetColumn(i, "USD") || ds_list2.GetColumn(i, "IHD") > ds_list2.GetColumn(i, "UHJ") ){
					var arr = [parseInt(i + 1), "희망일과 계약기간을 확인하십시오."];
					gfn_showMsg("CW00055", arr);			
					Tab00.tab2.grd_list.SetCellPos(9); 
					return;
				}
			}

			/* 협력사 요청 필수 해제 20150713

			if(length(trim(ds_list2.GetColumn(i, "EST"))) == 0){
				var arr = [parseInt(i + 1), Tab00.tab2.grd_list.GetCellProp("Head",10,"text")];
				gfn_showMsg("CW00055", arr);			
				Tab00.tab2.grd_list.SetCellPos(10); 
				return;
			}
			if(length(trim(ds_list2.GetColumn(i, "EAR"))) == 0){
				var arr = [parseInt(i + 1), Tab00.tab2.grd_list.GetCellProp("Head",11,"text")];
				gfn_showMsg("CW00055", arr);
				Tab00.tab2.grd_list.SetCellPos(11); 
				return;
			}

			*/
			//검사신청일 삭제 요청 201403
			// if(length(trim(ds_list2.GetColumn(i, "ISD"))) != 0 || length(trim(ds_list2.GetColumn(i, "CRD"))) != 0){
				// if(length(trim(ds_list2.GetColumn(i, "IDD"))) == 0){
					// var arr = [parseInt(i + 1), Tab00.tab2.grd_list.GetCellProp("Head",12,"text")];
					// gfn_showMsg("CW00055", arr);
					// Tab00.tab2.grd_list.SetCellPos(12); 
					// return;
				// }
			// }



			if(length(trim(ds_list2.GetColumn(i, "IDD"))) != 0){		
				if(ds_list2.GetColumn(i, "IDD") < ds_list2.GetColumn(i, "USD") || ds_list2.GetColumn(i, "IHD") > ds_list2.GetColumn(i, "UHJ") ){
					var arr = [parseInt(i + 1), "검사만료일과 계약기간을 확인하십시오."];
					gfn_showMsg("CW00055", arr);			
					Tab00.tab2.grd_list.SetCellPos(9); 
					return;
				}
			}

			ds_list3.AddRow();
			ds_list3.SetColumn(j, "FLAG", "U");
			ds_list3.SetColumn(j, "PJT", ds_list2.GetColumn(i, "PJT"));
			ds_list3.SetColumn(j, "HNO", ds_list2.GetColumn(i, "HNO"));
			ds_list3.SetColumn(j, "GNO", ds_list2.GetColumn(i, "GNO"));
			ds_list3.SetColumn(j, "EST", ds_list2.GetColumn(i, "EST"));
			ds_list3.SetColumn(j, "EAR", ds_list2.GetColumn(i, "EAR"));
			ds_list3.SetColumn(j, "IHD", ds_list2.GetColumn(i, "IHD"));
			ds_list3.SetColumn(j, "NTE", ds_list2.GetColumn(i, "NTE"));
			ds_list3.SetColumn(j, "ISD", '');
			ds_list3.SetColumn(j, "CRD", ds_list2.GetColumn(i, "CRD"));
			ds_list3.SetColumn(j, "IDD", ds_list2.GetColumn(i, "IDD"));
			ds_list3.SetColumn(j, "IEX", ds_list2.GetColumn(i, "IEX"));
			ds_list3.SetColumn(j, "OFFICE_CODE", ds_list2.GetColumn(i, "OFFICE_CODE"));
			ds_list3.SetColumn(j, "OFFICE_NAME", ds_list2.GetColumn(i, "OFFICE_NAME"));

			j++;
		}
	}
	TRACE(ds_list3.SaveXML());
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs12:CS1228001A_U1"
		, ""
		, "FLAG");
	tit_addSaveActionInfo(
		""
		, "cs12:CS1228001A_I1"
		, ""
		, "FLAG");

	tit_callService(
		""
		, ""
		, "ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query2();
	}
}

