function Tab00_OnChanged(obj,nOldIndex,nNewindex)
{
//탭 변경 직후 발생 이벤트 정의
	if(nNewIndex == 1) {//상세
		ufn_enableBtn("query",false);
		ufn_enableBtn("save" ,true);
		btn_upn.Enable = false;
		btn_trash.Enable = false;
		cb_gbn.Enable = false;

	} else {
		ufn_enableBtn("query",true);
		ufn_enableBtn("save" ,false);
		btn_upn.Enable = true;
		btn_trash.Enable = true;
		cb_gbn.Enable = true;

		fn_query();
	}	
}

function Tab00_OnChanging(obj,nOldIndex,nNewIndex)
{ // 계약정보 없는 경우 호기 탭 X
	if(nNewIndex == 1) {//상세
		if(ds_list.rowCount() == 0) {
			gfn_showMsg("CW00010");
			return false;
		} else {
			if(ds_list2.rowCount() == 0) {			
				gfn_showMsg("작성할 대상을 선택하십시오.");
				return false;
			} else {

			}
		}

	} else {

		fn_query();
	}
}


function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "EST") {
		if(ds_list2.GetColumn(nRow,"EST") == "I") {
			ds_ear_cd.Filter("SUBSTR(CODE,0,1) == 'I'");
		} else if(ds_list2.GetColumn(nRow,"EST") == "T"){
			ds_ear_cd.Filter("SUBSTR(CODE,0,1) == 'T'");
			ds_ear_cd.Filter("DTEXT1 <> 'X'"); // T10 > T01로 통합
		} 
		if(varOldValue <> varNewValue){
			ds_list2.SetColumn(nRow,"EAR", "");	
		}
	}
}

