}

/********************************************************************************
* 기      능   : 그리드 셀 선택시
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	/* 검사기관 DataSet Filter */
	var vColId = obj.GetCellProp("BODY",nCell,"colid");

	if (vColId == "EAR"){		//if(nCell == "19") {	
		if(ds_list2.GetColumn(nRow,"EST") == "I") {
			ds_ear_cd.Filter("SUBSTR(CODE,0,1) == 'I'");
		} else if(ds_list2.GetColumn(nRow,"EST") == "T"){
			ds_ear_cd.Filter("SUBSTR(CODE,0,1) == 'T'");
			ds_ear_cd.Filter("DTEXT1 <> 'X'"); // T10 > T01로 통합
		} else {
			var vCellIndex = obj.GetBindCellIndex("BODY","EST");
			var arr = [parseInt(nRow + 1), grd_list.GetCellProp("Head",vCellIndex,"text")];
			gfn_showMsg("CW00055", arr);
            grd_list.SetCellPos(vCellIndex); /* 커서 위치 설정 */  -- //grd_list.SetCellPos(18); /* 커서 위치 설정 */

			ds_ear_cd.UnFilter();
			return;
		}
	}
}
