/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save(){

	gfn_clearMsgBar();

    if(ds_list2.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}
	ds_list3.ClearData();

	var j = 0;
	for(var i=0; i<ds_list2.RowCount(); i++) {					

		if(ds_list2.GetColumn(i, "CHK") == true) {		

			// if(length(trim(ds_list2.GetColumn(i, "IHD"))) == 0){
				// var arr = [parseInt(i + 1), grd_list.GetCellProp("Head",19,"text")];
				// gfn_showMsg("CW00055", arr);			
				// grd_list.SetCellPos(16); 
				// return;
			// }

			if(ds_list2.GetColumn(i, "IHD") < ds_list2.GetColumn(i, "USD") || ds_list2.GetColumn(i, "IHD") > ds_list2.GetColumn(i, "UHJ") ){
				var arr = [parseInt(i + 1), "희망일과 계약기간을 확인하십시오."];
				gfn_showMsg("CW00055", arr);			
				grd_list.SetCellPos(grd_list.GetBindCellIndex("BODY","IHD"));		//grd_list.SetCellPos(17); 
				return;
			}

			if(length(trim(ds_list2.GetColumn(i, "IDD"))) != 0){		
				if(ds_list2.GetColumn(i, "IDD") < ds_list2.GetColumn(i, "USD") || ds_list2.GetColumn(i, "IHD") > ds_list2.GetColumn(i, "UHJ") ){
					var arr = [parseInt(i + 1), "검사만료일과 계약기간을 확인하십시오."];
					gfn_showMsg("CW00055", arr);			
					grd_list.SetCellPos(grd_list.GetBindCellIndex("BODY","IDD")); 		//grd_list.SetCellPos(24); 
					return;
				}
			}

			ds_list3.AddRow();
			ds_list3.SetColumn(j, "FLAG", "U");
			ds_list3.SetColumn(j, "PJT", ds_list2.GetColumn(i, "PJT"));
			ds_list3.SetColumn(j, "HNO", ds_list2.GetColumn(i, "HNO"));
			ds_list3.SetColumn(j, "GNO", ds_list2.GetColumn(i, "GNO"));
			ds_list3.SetColumn(j, "EST", ds_list2.GetColumn(i, "EST"));
			ds_list3.SetColumn(j, "EAR", ds_list2.GetColumn(i, "EAR"));
			ds_list3.SetColumn(j, "IHD", ds_list2.GetColumn(i, "IHD"));
			ds_list3.SetColumn(j, "NTE", ds_list2.GetColumn(i, "NTE"));
			ds_list3.SetColumn(j, "ISD", '');
			ds_list3.SetColumn(j, "CRD", ds_list2.GetColumn(i, "CRD"));
			ds_list3.SetColumn(j, "IDD", ds_list2.GetColumn(i, "IDD"));
			ds_list3.SetColumn(j, "IEX", ds_list2.GetColumn(i, "IEX"));
			ds_list3.SetColumn(j, "OFFICE_NAME", ds_list2.GetColumn(i, "OFFICE_NAME"));
			ds_list3.SetColumn(j, "OFFICE_CODE", ds_list2.GetColumn(i, "OFFICE_CODE"));

			j++;
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs12:CS1228001A_U1"
		, ""
		, "FLAG");
	tit_addSaveActionInfo(
		""
		, "cs12:CS1228001A_I1"
		, ""
		, "FLAG");

	tit_callService(
		""
		, ""
		, "ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

