function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	med_yymm.Value = substr(gfn_getCurrDate(),0,6);

	ed_pjt.SetFocus();

	var codeArr = ["VKGRP","VKBUR","CS129","CS203A"];
    var dsObjArr = [ds_vkgrp,ds_vkbur,ds_knd_cd,ds_inspect_region];
    var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr, true);

	// 초기화 완료메시지
	gfn_showMsg("CI00007");

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1228003A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	confirm_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();	

	if(confirm_auth == true) {
		ADMIN = true;
		btn_KESA_upload.Visible = true;
		btn_confirm.Visible     = true;
		//btn_detail.Visible = true;
	}	

}
var ADMIN = false ;
var Auth10 = false ; // 협력사 여부 
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	btn_KESA_upload.Visible = false;
	btn_confirm.Visible     = false;
	cb_inspect_region.Index = 0;

	var user_grp = gfn_getUserGroup();	
	//협력사 자료추가 불가능 
	if(user_grp == "10") {
		btn_detail.Visible = false;
		btn_receipt2.Visible = false;
		Auth10 = true;
	}
	btn_receipt.Enable  = false ;
	btn_receipt2.Enable = false ;
	btn_cancel.Enable   = false ;

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
