// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	// 메시지 처리 
	gfn_showMsg("CI00002", ds_list.RowCount()+"");


	//*******************************************************************************//
	// 승강기번호와 매칭되는 호기정보 없는 리스트 별도 통계 제공 
	if(ds_sid_list.RowCount() > 0 ) {

		st_text.Text = "승강기번호 누락 건수 :  " + ds_sid_list.Sum("CNT") ;
		btn_sid_detail.Visible = true;
	} else {

		st_text.Text = "" ;
		btn_sid_detail.Visible = false;
	}
	//*******************************************************************************//

	btn_receipt.Enable  = false ;
	btn_receipt2.Enable = false ;
	btn_cancel.Enable   = false ;
	btn_confirm.Enable  = false;

	DOC_PST = ds_cond.GetColumn(0, "PST");

	// 협력사 조회 및 엑셀 출력만 가능 
	if(Auth10) break;

	if (ds_list.RowCount() > 0) {

		if(DOC_PST == "A1") {
			btn_cancel.Enable  = true ;
			btn_receipt.Enable = true ;
			btn_receipt2.Enable = true ;
		}
		// 접수 상태의 경우 취소 활성화 ( 접수->미접수 상태변경 )
		if(DOC_PST == "A2") {
			btn_cancel.Enable  = true ;
			btn_confirm.Enable = true;
		}
		// 반송 상태의 경우 취소 활성화 ( 반송->접수 상태변경 )
		if(DOC_PST == "A3") {
			btn_cancel.Enable   = true ;
		}
		// 접수확정 상태의 경우 취소 활성화 ( 접수확정->접수 상태변경 )
		if(DOC_PST == "A4") {
			btn_cancel.Enable   = true ;
		}
		// 개별 접수 상태의 경우 취소 활성화 ( 개별 접수->미접수 상태변경 )
		if(DOC_PST == "Z1") {
			btn_cancel.Enable  = true ;
			btn_confirm.Enable = true;
		}
	}

}

