/********************************************************************************
* 기      능   : 검사 접수 요청 : 지사 - > 총괄 
********************************************************************************/
function fn_receipt()
{
	fn_makeZcst219("A2","");
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_makeZcst219(pst,case_flag) {

	ds_list_copy.ClearData();
	var j =0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			// 프로젝트 정보 없는 경우 미처리 
			if(length(trim(ds_list.GetColumn(i, "PJT"))) == 0) {
				gfn_showAlert(ds_list.GetColumn(i, "SID") + " : 승강기번호와 매칭되는 현장정보가 없어 접수가 불가합니다.");
				return;
			}

			// 승강기번호 중복 정보인 경우 미처리 
			if(ds_list.GetColumn(i, "DUP_FLAG") <> "Y") {

				ds_list_copy.AddRow();
				ds_list_copy.CopyRow(j,ds_list,i);
				ds_list_copy.SetColumn(j,"PST",pst);
				ds_list_copy.SetColumn(j,"CASE_FLAG",case_flag);

				j++;
			} else {
				if(ADMIN) {
					ds_list_copy.AddRow();
					ds_list_copy.CopyRow(j,ds_list,i);
					ds_list_copy.SetColumn(j,"PST",pst);
					ds_list_copy.SetColumn(j,"CASE_FLAG",case_flag);
					j++;
				} 
				else {
					gfn_showAlert(ds_list.GetColumn(i, "SID") + " : 승강기번호가 중복되어 접수가 불가합니다.");
					return;
				}
			}
		}
	}

	if(ds_list_copy.RowCount() == 0 ) {
		gfn_showAlert("CW00013");
		return;
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs12:CS1228003A_I1");
	tit_callService(
	""
	, ""
	, "ds_list_copy=ds_list_copy"
	, ""
	, ""
	, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("CI00003");
		fn_query();
	}
}

