/********************************************************************************
* 기      능   : 확정 
********************************************************************************/
function btn_confirm_OnClick(obj)
{
	ds_list_copy.ClearData();

	var j =0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {

			ds_list_copy.AddRow();

			ds_list_copy.CopyRow(j,ds_list,i);

			j++;
		}
	}

	if(ds_list_copy.RowCount() == 0 ) {
		gfn_showAlert("CW00013");
		return;
	}
//	trace(ds_list_copy.SaveXML());

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs12:CS1228003A_U2");	
	tit_callService(
	""
	, ""
	, "ds_list_copy=ds_list_copy"
	, ""
	, ""
	, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 접수 취소 
********************************************************************************/
function fn_cancel(obj)
{
	ds_list_copy.ClearData();

	var j =0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {

			ds_list_copy.AddRow();
			ds_list_copy.CopyRow(j,ds_list,i);
			if(DOC_PST == "A3" || DOC_PST == "A4") {
				if(ds_list.GetColumn(i, "CASE_FLAG") == "Y") {
					ds_list_copy.SetColumn(j,"PST","Z1");
				} else {
					ds_list_copy.SetColumn(j,"PST","A2");
				}
			} 
			j++;
		}
	}

	if(ds_list_copy.RowCount() == 0 ) {
		gfn_showAlert("CW00013");
		return;
	}
//	trace(ds_list_copy.SaveXML());

	tit_clearActionInfo();
	if(DOC_PST == "A1") {
		tit_addMultiActionInfo("cs12:CS1228003A_D2");	// 자료추가의 건에 대해서 ZCST218 삭제 
	} else if(DOC_PST == "A2" || DOC_PST == "Z1") {
		tit_addMultiActionInfo("cs12:CS1228003A_D1"); 	// ZCST219 삭제 
	} else if(DOC_PST == "A3" || DOC_PST == "A4") {
		tit_addMultiActionInfo("cs12:CS1228003A_U3"); 	// ZCST219 상태 변경  PST = 'A2'
	} 
	tit_callService(
	""
	, ""
	, "ds_list_copy=ds_list_copy"
	, ""
	, ""
	, "fn_afterDelete");

}
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showAlert("CE00001"); // 처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.
	} else {
		if(DOC_PST == "A1") {
			//gfn_showAlert("CI00004"); // 삭제를 완료했습니다.
		} else  {
			gfn_showAlert("CI00005"); // 처리를 완료했습니다.
		}
		fn_query();
	}
}
