//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	cb_cgbn.Index = 2;

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-3,10);
	cb_month1.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_month2.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_year1.SetFocus();
}

/************************************************************************************
* 기      능   : 작업기간 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
************************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_year1.index = eNum;
	cb_year2.index = eNum;
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	//년도필드 필수체크	
	if(gfn_isNull(cb_year1, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_year2, true, st_yymm.value))
		return;
	//월필드 필수체크	
	if(gfn_isNull(cb_month1, true, st_yymm.value))
		return;
	if(gfn_isNull(cb_month2, true, st_yymm.value))
		return;

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

    var yy1;
    var yy2;
    var mm1;
    var mm2;
    var tmm;

    if(st_ym.Value == "A") { //기준년월
		tmm = 1;
	} else { //견적년월
		tmm = 0;
	}

    if(cb_month1.Value == "01" && st_ym.Value == "A") {
		yy1 = (toNumber(cb_year1.Value) - 1);
		mm1 = "12";
	} else {
		yy1 = ds_cond.GetColumn(0,"YY1");
		if(st_ym.Value == "A") { //기준년월
			if(cb_month1.Value <= "10") {
				mm1 = "0"+(toNumber(cb_month1.Value) - tmm);
			} else {
				mm1 = (toNumber(cb_month1.Value) - tmm);
			}
		} else { //견적년월
			if(cb_month1.Value < "10") {
				mm1 = "0"+(toNumber(cb_month1.Value) - tmm);
			} else {
				mm1 = (toNumber(cb_month1.Value) - tmm);
			}
		}
	}

    if(cb_month2.Value == "01" && st_ym.Value == "A") {
		yy2 = (toNumber(cb_year2.Value) - 1);
		mm2 = "12";
	} else {
		yy2 = ds_cond.GetColumn(0,"YY2");
		if(st_ym.Value == "A") { //기준년월
			if(cb_month2.Value <= "10") {
				mm2 = "0"+(toNumber(cb_month2.Value) - tmm);
			} else {
				mm2 = (toNumber(cb_month2.Value) - tmm);
			}
		} else { //견적년월
			if(cb_month2.Value < "10") {
				mm2 = "0"+(toNumber(cb_month2.Value) - tmm);
			} else {
				mm2 = (toNumber(cb_month2.Value) - tmm);
			}
		}
	}

    ds_cond.SetColumn(0,"YYMM1",yy1+mm1);
    ds_cond.SetColumn(0,"YYMM2",yy2+mm2);
	ds_cond.SetColumn(0, "PJT", ed_pjt.value);

    if(cb_cgbn.Value == "") { //전체
		ds_cond.SetColumn(0, "CGBN", "");
	} else if(cb_cgbn.Value == "Y") { 
		ds_cond.SetColumn(0, "CGBN", "Y");
	} else if(cb_cgbn.Value == "N") { 
		ds_cond.SetColumn(0, "CGBN", "N");
	}

	var t_sql = "";

	if(st_ym.Value == "A") { //기준년월
		t_sql = "cs12:CS1231001A_S1";
	} else { //견적년월
		t_sql = "cs12:CS1231001A_S2";
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo(t_sql);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_list2.ClearData();
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

