* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		//저장할 정보 없음. 
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	ds_list2.ClearData();

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(length(trim(ds_list.GetColumn(i, "QTDAT"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 견적제출일을 입력하시기 바랍니다.");
				return false;
			}
			if(length(trim(ds_list.GetColumn(i, "RECOST"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 원격비용을 입력하시기 바랍니다.");
				return false;
			}	
			if(length(trim(ds_list.GetColumn(i, "DIPBX"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 DI-PBX를 입력하시기 바랍니다.");
				return false;
			}	
			if(length(trim(ds_list.GetColumn(i, "MAMT"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 월정보수료를 입력하시기 바랍니다.");
				return false;
			}	
			if(length(trim(ds_list.GetColumn(i, "YUSD"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 계약개시일을 입력하시기 바랍니다.");
				return false;
			}	
			if(length(trim(ds_list.GetColumn(i, "YUHJ"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 계약종료일을 입력하시기 바랍니다.");
				return false;
			}	
			if(length(trim(ds_list.GetColumn(i, "YBSTDK"))) == 0) {
				gfn_showAlert(i + 1 + "번째 호기의 수주예정일을 입력하시기 바랍니다.");
				return false;
			}	
			if(trim(ds_list.GetColumn(i, "BMT")) > '') {
				if(parseInt(trim(ds_list.GetColumn(i, "BMT"))) >= parseInt(trim(ds_list.GetColumn(i, "YUSD")))) {
					gfn_showAlert(i + 1 + "번째 호기의 계약개시일이 무상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
					return false;
				}
			}
			if(trim(ds_list.GetColumn(i, "UHJ")) > '') {
				if(parseInt(trim(ds_list.GetColumn(i, "UHJ"))) >= parseInt(trim(ds_list.GetColumn(i, "YUSD")))) {
					gfn_showAlert(i + 1 + "번째 호기의 계약개시일이 유상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
					return false;
				}
			}
			if(trim(ds_list.GetColumn(i, "BMT")) > '') {
				if(parseInt(trim(ds_list.GetColumn(i, "BMT"))) >= parseInt(trim(ds_list.GetColumn(i, "YBSTDK")))) {
					gfn_showAlert(i + 1 + "번째 호기의 수주예정일이 무상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
					return false;
				}
			}
			if(trim(ds_list.GetColumn(i, "UHJ")) > '') {
				if(parseInt(trim(ds_list.GetColumn(i, "UHJ"))) >= parseInt(trim(ds_list.GetColumn(i, "YBSTDK")))) {
					gfn_showAlert(i + 1 + "번째 호기의 수주예정일이 유상만료일보다 작습니다.\n다시 입력하시기 바랍니다.");
					return false;
				}
			}
			if(parseInt(trim(ds_list.GetColumn(i, "YUSD"))) >= parseInt(trim(ds_list.GetColumn(i, "YUHJ")))) {
				gfn_showAlert(i + 1 + "번째 호기의 계약종료일이 계약개시일보다 작습니다.\n다시 입력하시기 바랍니다.");
				return false;
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			ds_list2.SetColumn(j, "UPN2", ds_list.GetColumn(i, "UPN"));
			ds_list2.SetColumn(j, "CST2", ds_list.GetColumn(i, "CST"));
			ds_list2.SetColumn(j, "PJT2", ds_list.GetColumn(i, "PJT"));
			ds_list2.SetColumn(j, "HNO2", ds_list.GetColumn(i, "HNO"));
			ds_list2.SetColumn(j, "SEQ2", ds_list.GetColumn(i, "SEQ"));
			ds_list2.SetColumn(j, "QTDAT2", ds_list.GetColumn(i, "QTDAT"));
			ds_list2.SetColumn(j, "RECOST2", ds_list.GetColumn(i, "RECOST"));
			ds_list2.SetColumn(j, "DIPBX2", ds_list.GetColumn(i, "DIPBX"));
			ds_list2.SetColumn(j, "MAMT2", ds_list.GetColumn(i, "MAMT"));
			ds_list2.SetColumn(j, "KND2", ds_list.GetColumn(i, "KND"));
			ds_list2.SetColumn(j, "YUSD2", ds_list.GetColumn(i, "YUSD"));
			ds_list2.SetColumn(j, "YUHJ2", ds_list.GetColumn(i, "YUHJ"));
			ds_list2.SetColumn(j, "ETERM2", ds_list.GetColumn(i, "ETERM"));
			ds_list2.SetColumn(j, "YBSTDK2", ds_list.GetColumn(i, "YBSTDK"));
			ds_list2.SetColumn(j, "BIGO2", ds_list.GetColumn(i, "BIGO"));
			j++;
		}			
	}

	tit_clearActionInfo();

	// 원격감시 시스템 계약대상 insert(zcst185)
	tit_addSaveActionInfo(
		""
		, "cs12:CS1231001A_I1"
		, ""
		, "FLAG");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list2:A"
        , ""
        , ""
        , "fn_afterSave");
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		// 전체 선택
		gfn_selectAllGrid2(obj,0,0,true);
		//calc_Amt();
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 엑셀
********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list, true);
}

function btn_pjt_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	var t_amt = 0;
	if(strColumnID == "RECOST" || strColumnID == "DIPBX" || strColumnID == "MAMT")  {
		t_amt = parseFloat(ds_list.GetColumn(nRow, "RECOST")) + parseFloat(ds_list.GetColumn(nRow, "DIPBX")) + parseFloat(ds_list.GetColumn(nRow, "MAMT"));
		ds_list.FireEvent = false;
		ds_list.SetColumn(nRow, "QTAMT", t_amt);
		ds_list.FireEvent = true;
	}

	if(strColumnID == "QTDAT" || strColumnID == "RECOST" || strColumnID == "DIPBX" || strColumnID == "MAMT" || 
	   strColumnID == "KND" || strColumnID == "YUSD" || strColumnID == "YUHJ" || strColumnID == "ETERM" ||
	   strColumnID == "YBSTDK" || strColumnID == "BIGO") {
		ds_list.FireEvent = false;
		ds_list.SetColumn(nRow, "CHK", "1");
		ds_list.FireEvent = true;
	}
}

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	var rect = obj.GetCellRect(nRow, ncell);

	var nX = ClientToScreenX(obj,rect[0]);
	var nY = ClientToScreenY(obj,rect[1] + 20);

	PopupDiv0.TrackPopup(nX,nY);

	if(nCell == 17) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "QTDAT");
	} else if(nCell == 23) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "YUSD");
	} else if(nCell == 24) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "YUHJ");
	} else if(nCell == 26) {
		PopupDiv0.cal_temp.value = ds_list.getColumn(nRow, "YBSTDK");	
	}

	PopupDiv0.cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우
********************************************************************************/
function PopupDiv0_cal_temp_OnKillFocus(obj) {
	PopupDiv0.ClosePopup();
}

function PopupDiv0_cal_temp_OnDayClick(obj,strText) {
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 17) {
		ds_list.SetColumn(ds_list.row, "QTDAT", strText);
	} else if(grd_list.GetClickedCell() == 23) {
		ds_list.SetColumn(ds_list.row, "YUSD", strText);
	} else if(grd_list.GetClickedCell() == 24) {
		ds_list.SetColumn(ds_list.row, "YUHJ", strText);
	} else if(grd_list.GetClickedCell() == 26) {
		ds_list.SetColumn(ds_list.row, "YBSTDK", strText);	
	}

	PopupDiv0.ClosePopup();
}

function btn_copy_OnClick(obj) {
	if(ds_list.rowCount() < 1) {
		// 삭제할 정보 없음. 
		return;
	}

	var arg = "rows=" + quote(ds_list.lastrow+1);
	gfn_openPopup("CS1231001B", true, arg);
}
