#include "LIB::commons.js";	// 공통 스크립트 
#include "LIB::utility.js";	// 다중 선택 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var pjt = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();
	fn_query();
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	ds_cond2.SetColumn(0, "PJT", pjt);
	ds_cond2.SetColumn(0, "TEM", ds_list.getColumn(ds_list.currow, "TEM"));
	ds_cond2.SetColumn(0, "USR", G_USER_NAME);
	ds_cond2.SetColumn(0, "YDT", substr(gfn_getCurrDate(),2,2));
	ds_cond2.SetColumn(0, "MDT", substr(gfn_getCurrDate(),4,2));
    ds_cond2.SetColumn(0, "TEM_NM", ds_list.getColumn(ds_list.currow, "TEM_NM"));

	tit_clearActionInfo();	
	//tit_addSearchActionInfo("cs12:CS1240001E_S1");
	tit_addSearchActionInfo("cs12:CS1240001E_S3"); //2015.03.31 YAR 초기데이터 or 저장이력있는 데이터 출력
	tit_addSearchActionInfo("cs12:CS1240001E_S4"); //2015.04.06 YAR 저장여부 확인
	tit_callService(
		""
		, ""
		, "ds_cond2=ds_cond2"
		, "ds_save=ds_save ds_save2=ds_save2"  //ds_head
		, ""
		, "fn_afterQuery"
		, true
		, true);
}

function fn_query2() {

	ds_cond3.ClearData();
	ds_cond3.AddRow();
	ds_cond3.SetColumn(0, "TEM", ed_tem.Text);
	ds_cond3.SetColumn(0, "YDT", ed_ydt.Text);
	ds_cond3.SetColumn(0, "MDT", ed_mdt.Text);
	ds_cond3.SetColumn(0, "SEQ", ed_seq.Text); 

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs12:CS1240001E_S3");
	tit_callService(
		""
		, ""
		, "ds_cond2=ds_cond2"
		, "ds_head=ds_head"
		, ""
		, "fn_afterQuery2"
		, true
		, true);

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	trace(ds_save.SaveXML());
	trace(ds_save2.SaveXML());
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	ed_pjt.Enable = false;
	ed_nam.Enable = false;
	ed_usr.Enable = false;
	ed_adr.Enable = false;
	ed_pho.Enable = false;
	ed_txt.Enable = false;
	ed_ref.Enable = false;
	cal_bmt.Enable = false;
	cal_rqt.Enable = false;
	ed_num.Enable = false;	
}


/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(length(ed_pjt.Text) >6)
	{
		gfn_showAlert("프로젝트번호 자릿수가 맞지 않습니다");
		return false;
	}	
//	ds_save.ClearData();
//	ds_save.AddRow();
	ds_save.SetColumn(0, "FLAG",    		"U"); 
	ds_save.SetColumn(0, "PJT",    		ed_pjt.Text); 
	ds_save.SetColumn(0, "USR",   		ed_usr.Text);
	ds_save.SetColumn(0, "PHO",    		ed_pho.Text);
	ds_save.SetColumn(0, "NAM",   		ed_nam.Text);
	ds_save.SetColumn(0, "ADR",  		ed_adr.Text);
	ds_save.SetColumn(0, "TXT",  		ed_txt.Text);
	ds_save.SetColumn(0, "BMT",   		cal_bmt.Value);
	ds_save.SetColumn(0, "NUM",    		ed_num.Text);
	ds_save.SetColumn(0, "REF",    		ed_ref.Text);
	ds_save.SetColumn(0, "RQT",     	cal_rqt.Value);
	ds_save.SetColumn(0, "TEM",     	ed_tem.Text);
	ds_save.SetColumn(0, "YDT",     	ed_ydt.Text);
	ds_save.SetColumn(0, "MDT",     	ed_mdt.Text);
	ds_save.SetColumn(0, "DTEXT2",	    ed_dtext2.Text);
	ds_save.SetColumn(0, "DTEXT3",	    ed_dtext3.Text);
	ds_save.SetColumn(0, "DTEXT4",	    ed_dtext4.Text);
	ds_save.SetColumn(0, "TEM_NM",	    ed_tem_nm.Text);
	ds_save.SetColumn(0, "SEQ",         ed_seq.Text);
	ed_num.Enable = false;
	tit_clearActionInfo();
	// tit_addSaveActionInfo(
		// ""
		// , "cs12:CS1240001E_I2"
		// , ""
		// , "FLAG");
	tit_addSingleActionInfo("cs12:CS1240001E_I2");
	tit_callService(
        ""
        , ""
        , "ds_save=ds_save"
        , ""
        , ""
        , "fn_afterSave");

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();		
	}	
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 안내문 출력
********************************************************************************/
function btn_info_OnClick(obj)
{
	trace(ds_save.GetColumn(0,"RQT"));
	if(ed_num.Text = "")
	{
		gfn_showAlert("문서번호가 존재하지 않습니다");
		return false;
	}
	if(ds_save2.GetColumn(0, "CHK") == "0"){	//2015.04.06 YAR 데이터 저장처리 하지 않은경우 Exception 
		gfn_showAlert("저장처리 후 안내문 출력이 가능합니다.");
		return false;
	}
	Dialog("CS12::CS1240001F.xml", "");
}

/********************************************************************************
* 기      능   : 공문 출력  
********************************************************************************/
function btn_app_OnClick(obj)
{
	if(ed_num.Text = "")
	{
		gfn_showAlert("문서번호가 존재하지 않습니다");
		return false;
	}
	if(ds_save2.GetColumn(0, "CHK") == "0"){	//2015.04.06 YAR 데이터 저장처리 하지 않은경우 Exception 
		gfn_showAlert("저장처리 후 공문 출력이 가능합니다.");
		return false;
	}
	Dialog("CS12::CS1240001G.xml", "");
}
