//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup;
var gRow = null;
var t_costT; //원가
var t_costT2; //원가(투입예상자재)
var t_cos1; //간접비제외 원가
var t_cos2; //간접비제외 원가(투입예상자재)
var t_costA; //직접비
var t_costA_2; //직접비(투입예상자재)
var t_costB; //간접비
var t_costB_2; //간접비(투입예상자재)
var t_costC; //HRTS
var t_costD; //DI-PBX
var t_nmt; //직접노무비
var t_jmt; //직접재료비
var t_jmt2; //직접재료비(투입예상자재)
var t_kmt; //직접경비
var t_hmt; //HRTS비용
var t_dmt; //DI-PBX비용
var t_cmt; //검사수수료
var t_gmt; //간접비
var t_gmt2; //간접비(투입예상자재)
var chk_fst = 0;
var chk_fst2 = 0;
var chk_fst3 = 0;
var chk_fst4 = 0;
var fa_reqno;
var currRow = 0;
var sendChk = false;
var mode;

var vCos_auth = false; // 원가내역권한 버튼
var vDel_auth = false; // 결재상신 이후 삭제버튼권한
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();

    var codeArr = ["WBAP_STAT","CS123"];
    var dsObjArr = [ds_apstat,ds_tbld];
    var addTypeArr = [G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr, true);

	// 버튼초기화
	div_btn.btn_decide.Enable 	= false;	// 결재버튼
	div_btn.btn_sendback.Enable = false;  	// 반송버튼
	//====================== 원가내역조회를 위한 '원가내역조회'버튼 권한 설정
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1240003B");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vCos_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	//=======================================================================
	//====================== 결재상신이후 삭제버튼 활성화 권한 설정 =========
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1240003B");
	ds_rcond.SetColumn(0, "GBN", "02");	
	vDel_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	//=======================================================================


	if(lnk == "INS") { //신규견적 작성시
		div_btn.btn_recost.Enable = false;
		div_btn.btn_delete.Enable = false;
		div_btn.btn_print.Enable = false;
		div_btn.btn_trans.Enable = false;
		fn_setHno();
	} else if(lnk == "PLN") { //이동계획에서 작성시
		div_btn.btn_recost.Enable = false;
		div_btn.btn_delete.Enable = false;
		div_btn.btn_print.Enable = false;
		div_btn.btn_trans.Enable = false;
		fn_setPlnHno();
	} else { //기존견적 수정시
		fn_selHno();
	}
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	var user_grp = gfn_getUserGroup();

	if(user_grp == "40") {
		btn_saveopn.Visible = true;
	} else {
		btn_saveopn.Visible = false;
	}
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

function fn_btnInit() {
	userGroup = gfn_getUserGroup();

	if(ds_head.GetColumn(0, "PST") == "A1") {
		div_btn.btn_save.Enable = true;
		div_btn.btn_delete.Enable = true;
		div_btn.btn_trans.Enable = true;
		div_btn.btn_recost.Enable = false;
		div_btn.btn_print.Enable = false;

		cal_cdt.Enable = true;
		ed_nam.Enable = true;
		ed_spt.Enable = true;
		ed_pjt.Enable = true;
		ed_adr.Enable = true;
		ed_spc.Enable = true;
		cb_prd.Enable = true;

		grd_list2.Editable = true;
	} else if(ds_head.GetColumn(0, "PST") == "A2" || ds_head.GetColumn(0, "PST") == "A6") {
		div_btn.btn_save.Enable = false;		
		//if(G_USER_ID = "2020882") {
		if(vDel_auth == true) {
			div_btn.btn_delete.Enable = true;
		} else{
			div_btn.btn_delete.Enable = false;		
		}
		div_btn.btn_trans.Enable = false;
		div_btn.btn_recost.Enable = true;

		cal_cdt.Enable = true;
		ed_nam.Enable = true;
		ed_spt.Enable = true;
		ed_pjt.Enable = true;
		ed_adr.Enable = true;
		ed_spc.Enable = true;
		cb_prd.Enable = true;

		grd_list2.Editable = true;
	} else if(ds_head.GetColumn(0, "PST") > "A2" && ds_head.GetColumn(0, "PST") < "A3") {
		div_btn.btn_save.Enable = false;	
		//if(G_USER_ID = "2020882") {
		if(vDel_auth == true) {
			div_btn.btn_delete.Enable = true;
		} else{
			div_btn.btn_delete.Enable = false;
		}
		div_btn.btn_trans.Enable = false;
		div_btn.btn_recost.Enable = false;

		cal_cdt.Enable = false;
		ed_nam.Enable = false;
		ed_spt.Enable = false;
		ed_pjt.Enable = false;
		ed_adr.Enable = false;
		ed_spc.Enable = false;
		cb_prd.Enable = false;

		grd_list2.Editable = false;
	} else if(ds_head.GetColumn(0, "PST") == "A3") {
		div_btn.btn_save.Enable = false;
		//if(G_USER_ID = "2020882") {
//			div_btn.btn_delete.Enable = true;
		// }
		// } else{
			// div_btn.btn_delete.Enable = false;
		// }
		if(vDel_auth == true) {
//			div_btn.btn_delete.Enable = true;
		} else{
			div_btn.btn_delete.Enable = false;
		}
		div_btn.btn_trans.Enable = false;

		if(ds_head.GetColumn(0,"APSTAT") == '' || ds_head.GetColumn(0,"APSTAT") == '2' || ds_head.GetColumn(0,"APSTAT") == '4') //결재전, 내부반송, 수신반송 상태 
			div_btn.btn_recost.Enable = true;
		else
			div_btn.btn_recost.Enable = false;

		grd_list2.Editable = true;

		if(length(trim(fa_reqno)) > 0) {
			div_btn.btn_recost.enable = false;
			div_btn.btn_delete.enable = false;
			div_btn.btn_save.enable = false;
			div_btn.btn_trans.enable = false;

			if(ds_head.GetColumn(0, "APSTAT") == ""){ 
				btn_app.Visible = true;
			}else if(ds_head.GetColumn(0, "APSTAT") == "3") {
				if(trim(fa_popgbn) == "B") { //부서수신함
					btn_app.Visible = true;
				} else { //결재문서함
					btn_app.Visible = false;
				}
			} else {
				btn_app.Visible = false;
			}

			btn_matnr.Visible = false;
			btn_addmat.Visible = false;
		}
	}

	//if(G_USER_ID == "1001329" || G_USER_ID == "2010984" || G_USER_ID == "2017938" || G_USER_ID == "2021056" || 
	//   G_USER_ID == "2017750" || G_USER_ID == "2021200" || G_USER_ID == "2023687" || G_USER_ID == "2018900" || 
	//   G_USER_ID == "2029758" || G_USER_ID == "2999999") {
	if (vCos_auth == true){
		btn_cos.Visible = true;
	} else{
		btn_cos.Visible = false;
	}

	if( (ds_head.GetColumn(0,"APSTAT") > '0' && ds_head.GetColumn(0,"APSTAT") != '3' && ds_head.GetColumn(0,"APSTAT") < '6') && ds_decide.rowcount > 0 && G_USER_ID == substr(ds_decide.GetColumn(0, "APNUM"),1) ) {
		div_btn.btn_decide.Enable   = true;		// 결재버튼
		div_btn.btn_sendback.Enable = true;		// 반송버튼
	} else {
		div_btn.btn_decide.Enable = false;		// 결재버튼
		div_btn.btn_sendback.Enable = false;	// 반송버튼
	}

	//권한에 따른 보기 설정
	var user_grp = gfn_getUserGroup();

	if(user_grp == "10"){	//출력, 시행율 안보여 주기
		div_btn.btn_print.Enable = false;
		st_ra1.Visible = false;
		ed_ra1.Visible = false;
		st_ra2.Visible = false;
		ed_ra2.Visible = false;
		btn_cos.Visible = false;
	}

	/*
	alert(
		"PST :: " + ds_head.GetColumn(0,"PST") + "\n" +
		"APSTAT :: " + ds_head.GetColumn(0,"APSTAT") + "\n" +
		"fa_reqno :: " + fa_reqno + "\n" +
		"G_USER_ID :: " + G_USER_ID + "\n" +
		"btn_decide -- " + div_btn.btn_decide.Enable
	);
	*/

	fn_calcAmt();
}

/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_queryIns() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1240003B_S1");
	tit_addSearchActionInfo("cs12:CS1240003B_S2");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_head=ds_head ds_list2=ds_list2"
        , ""
        , "fn_afterQueryIns"
        , true
        , true);
}

function fn_queryUpd() {
	tit_clearActionInfo();

	if(fa_reqno == null || length(trim(fa_reqno)) == 0) {
		tit_addSearchActionInfo("cs12:CS1240003B_S3");
		tit_addSearchActionInfo("cs12:CS1240003B_S4");
		tit_addSearchActionInfo("cs12:CS1240003B_S12");
		tit_addSearchActionInfo("cs12:CS1240003B_S18");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond2"
			, "ds_head=ds_head ds_list2=ds_list2 ds_his=ds_his ds_detail_pri=ds_detail_pri"
			, ""
			, "fn_afterQueryUpd"
			, true
			, true);
	} else {
		tit_addSearchActionInfo("cs12:CS1240003B_S8");
		tit_addSearchActionInfo("cs12:CS1240003B_S9");
		tit_addSearchActionInfo("cs12:CS1240003B_S13");
		tit_addSearchActionInfo("cs12:CS1240003B_S19");

		tit_addSearchActionInfo("wb01:APP_STATUS");

		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond2"
				, "ds_head=ds_head ds_list2=ds_list2 ds_his=ds_his ds_detail_pri=ds_detail_pri ds_decide=ds_decide"
				, ""
				, "fn_afterQueryUpd"
				, true
				, true);
	}


}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryIns(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");
	cb_mmn.Index = 0;

	if(ds_head.GetColumn(0, "PST") == "A3") {
		div_btn.btn_recost.enable = true;
	}
}

function fn_afterQueryUpd(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");

	chk_fst = 0;
	chk_fst2 = 0;
	chk_fst3 = 0;
	chk_fst4 = 0;

	if(ds_head.GetColumn(0, "APSTAT") == "") {
		btn_app.Visible = true;
	} else {
		btn_app.Visible = false;
	}

	/*
	if(ds_head.GetColumn(0, "MATOT") > 0 && ds_head.GetColumn(0, "AMG") == "Y") { // 투입예상자재
		btn_matnr.Visible = false;
		btn_addmat.Visible = true;
	} else if (ds_head.GetColumn(0, "MATOT") > 0 && ds_head.GetColumn(0, "AMG") == "N") {	// 추가자재
		btn_matnr.Visible = true;
		btn_addmat.Visible = false;
	}
	*/
	fn_btnInit();
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(length(ds_head.GetColumn(0, "APSTAT")) > 0) {
		gfn_showAlert("결재상신후 수정은 불가능합니다.");
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "ETN"))) == 0 && ds_head.GetColumn(0, "PST") >= "A1") {
		gfn_showAlert("진행상태를 확인하십시요.");
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "ETN"))) > 0 && (length(trim(ds_head.GetColumn(0, "PST"))) == 0 || ds_head.GetColumn(0, "PST") > "A2")) {
		gfn_showAlert("진행상태를 확인하십시요.");
		return false;
	}

	if(ds_head.GetColumn(0, "STD") == "") {
		gfn_showAlert("계약시작일을 확인하십시요.");
		return false;
	}

	if(ds_head.GetColumn(0, "ETD") == "") {
		gfn_showAlert("계약종료일을 확인하십시요.");
		return false;
	}

	if(parseInt(ds_head.GetColumn(0, "ETD")) <= parseInt(ds_head.GetColumn(0, "STD"))){
		gfn_showAlert("계약기간을 확인하십시오.");
		return false;
	}

	if(ds_head.GetColumn(0, "CDT") == "") {
		gfn_showAlert("계약예정일을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CPD") == "") {
		gfn_showAlert("계약상품을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "NAM") == "") {
		gfn_showAlert("고객명을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SPT") == "") {
		gfn_showAlert("현장명을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CND") == "") {
		gfn_showAlert("계약유형을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "ADR") == "") {
		gfn_showAlert("현장주소를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "GBU") == "") {
		gfn_showAlert("직영구분을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CNT") == "") {
		gfn_showAlert("대수를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "FLR") == "") {
		gfn_showAlert("층수를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SPD") == "") {
		gfn_showAlert("속도를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "YGB") == "") {
		gfn_showAlert("영업주체를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CYN") == "") {
		gfn_showAlert("CRT유무를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "PRD") == "") {
		gfn_showAlert("점검주기를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "BLD") == "") {
		gfn_showAlert("건물용도를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SRG") == "") {
		gfn_showAlert("Sheave/Rope를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "PDT") == "") {
		gfn_showAlert("제품군을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SYR") == "") {
		gfn_showAlert("설치연령을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "RMK") == "") {
		gfn_showAlert("특이사항을 확인하십시요.");
		return false;
	}

	for(var i=0; i<ds_list2.rowcount; i++) {		
		if(ds_head.GetColumn(0, "CPD") == "A" && (ds_list2.GetColumn(0, "FMT") == 0 || length(trim(ds_list2.GetColumn(0, "FMT"))) == "" )) {
			gfn_showAlert("FM보수료는 필수입력 사항입니다.");
			return false;
		}

		if(ds_head.GetColumn(0, "CPD") == "B" && (ds_list2.GetColumn(0, "PMT") == 0 || length(trim(ds_list2.GetColumn(0, "PMT"))) == "")) {
			gfn_showAlert("POG보수료는 필수입력 사항입니다.");
			return false;
		}

		if(length(trim(ds_list2.GetColumn(0, "HWR"))) > 0 && ds_list2.GetColumn(0, "HMT") == 0) {
			gfn_showAlert("HRTS료는 필수입력 사항입니다.");
			return false;
		}

		if(length(trim(ds_list2.GetColumn(0, "DND"))) > 0 && ds_list2.GetColumn(0, "DMT") == 0) {
			gfn_showAlert("DI-PBX료는 필수입력 사항입니다.");
			return false;
		}

		ds_list2.SetColumn(i, "ETN", ds_head.GetColumn(0, "ETN"));
		ds_list2.SetColumn(i, "SIR", ds_head.GetColumn(0, "SIR"));

		if(parseInt(ds_list2.GetColumn(i, "PDN")) == 0 || length(trim(ds_list2.GetColumn(i, "PDN"))) == 0) {
			ds_list2.SetColumn(i, "PDN", 0);
		} 

		if(parseInt(ds_list2.GetColumn(i, "FMT")) == 0 || length(trim(ds_list2.GetColumn(i, "FMT"))) == 0) {
			ds_list2.SetColumn(i, "FMT", 0);
			ds_list2.SetColumn(i, "FMG", "N");
		} else {
			ds_list2.SetColumn(i, "FMG", "Y");
		}

		if(parseInt(ds_list2.GetColumn(i, "PMT")) == 0 || length(trim(ds_list2.GetColumn(i, "PMT"))) == 0) {
			ds_list2.SetColumn(i, "PMT", 0);
			ds_list2.SetColumn(i, "POG", "N");
		} else {
			ds_list2.SetColumn(i, "POG", "Y");
		}

		if(parseInt(ds_list2.GetColumn(i, "HMT")) == 0 || length(trim(ds_list2.GetColumn(i, "HMT"))) == 0) {
			ds_list2.SetColumn(i, "HMT", 0);
			ds_list2.SetColumn(i, "HGB", "N");
		} else {
			if(length(ds_list2.GetColumn(i, "HWR")) == 0 ){ // 회선종류
				var arr = [parseInt(i) + 1, st_hwr.value];
				gfn_showMsg("CW00055", arr);
				return;
			} else {
				ds_list2.SetColumn(i, "HGB", "Y");
			}
		}

		if(parseInt(ds_list2.GetColumn(i, "DMT")) == 0 || length(trim(ds_list2.GetColumn(i, "DMT"))) == 0) {
			ds_list2.SetColumn(i, "DMT", 0);
			ds_list2.SetColumn(i, "DGB", "N");
		} else {
			if(length(ds_list2.GetColumn(i, "DND")) == 0 ){ // DI-PBX종류
				var arr = [parseInt(i) + 1, st_dnd.value];
				gfn_showMsg("CW00055", arr);
				return;
			} else {
				ds_list2.SetColumn(i, "DGB", "Y");
			}
		}

		if(length(trim(ds_list2.GetColumn(i, "HWR"))) > 0) {
			ds_head.SetColumn(0, "HYN", "Y");
		} else {
			ds_head.SetColumn(0, "HYN", "N");
		}

		if(length(trim(ds_list2.GetColumn(i, "DND"))) > 0) {
			ds_head.SetColumn(0, "DYN", "Y");
		} else {
			ds_head.SetColumn(0, "DYN", "N");
		}
	}

	if(length(trim(ds_head.GetColumn(0, "TXT"))) > 0 && ds_head.GetColumn(0, "TOT") < 1) {	
		gfn_showAlert("CW10025","자재금액이 없습니다. 자재금액을 입력하세요");
		ed_tot.SetFocus();
		return false;
	}

	if(length(trim(ds_head.GetColumn(0, "TXT"))) == 0 && ds_head.GetColumn(0, "TOT") > 0) {
		gfn_showAlert("CW10025","자재내역이 없습니다. 자재내역을 입력하세요");
		ed_txt.SetFocus();
		return false;
	}

	var str_dat = 0.0;
	str_dat = round((parseInt(ds_head.GetColumn(0, "MMN")) / gfn_getMonthCnt(ds_head.GetColumn(0, "STD"), ds_head.GetColumn(0, "ETD"))) * 100,1);

	ds_head.SetColumn(0, "DAT", str_dat);

	if(ds_detail_pri.rowcount <= 0) {
		fn_make_period();
	} else {
		var tot = toNumber(ds_list2.TotalCaseSum("AMT>0","AMT"));
		var tot2= toNumber(ds_detail_pri.TotalCaseSum("TOT>0","STOT"));;

		tot = tot * gfn_getMonthCnt(cal_from.Value,cal_to.Value);

		if(tot == 0 || tot2 == 0 || tot != tot2){
			alert('총계약금액('+tot+')과 ' + '계약기간 별 금액합('+tot2+')이 틀립니다. \n'
				+ '계약기간 별 금액을 확인하십시요.'
			);
			return false;
		}
	}
	if(ds_head.GetColumn(0, "PNT") = "") ds_head.SetColumn(0, "PNT", "0");
	if(ds_head.GetColumn(0, "SAJ") = "") ds_head.SetColumn(0, "SAJ", "0");
	if(ds_head.GetColumn(0, "GIS") = "") ds_head.SetColumn(0, "GIS", "90.0");	//기성지급율
	if(ds_head.GetColumn(0, "TOT") = "") ds_head.SetColumn(0, "TOT", "0");	

	if(length(trim(ds_head.GetColumn(0, "ETN"))) == 0) {	//견적테이블에 내역이 없는경우
		ds_docno_gen.ClearData();													//결재번호생성 데이터셋을 클리어한다
		ds_docno_gen.AddRow();																
		ds_docno_gen.SetColumn(0,"PFX","H10");										//Prefix

		tit_clearActionInfo();					
		tit_addSearchActionInfo("wb01:DOC_CODE_GEN_S1",true);					
		tit_addSearchActionInfo("wb01:DOC_CODE_GEN_U1");					
		tit_callService(															//동기화 방식으로 서비스를 콜한다.	
			, ""				
			, "ds_docno_gen_i=ds_docno_gen"				
			, "ds_docno_gen=ds_docno_gen_o"				
			, ""				
			, ""				
			, true
			, false
			, true);				

		ds_head.SetColumn(0,"DOC",ds_docno_gen.GetColumn(0, "DOCNO"));

		tit_clearActionInfo();
//		tit_addSingleActionInfo("cs12:CS1240001B_U4");
		tit_addSingleActionInfo("cs12:CS1240003B_I1");
		tit_addMultiActionInfo("cs12:CS1240003B_I2");
		tit_addMultiActionInfo("cs12:CS1240003B_I6");
		tit_addSearchActionInfo("cs12:CS1240003B_S23");

		tit_callService(
			""
			, ""
			, "ds_head=ds_head ds_list2=ds_list2 ds_detail_pri=ds_detail_pri"
			, "ds_etn=ds_etn"
			, ""
			, "fn_afterSave"
			, true
			, false
			, true);
	} else {
		tit_clearActionInfo();
		tit_addSingleActionInfo("cs12:CS1240003B_U2");
		tit_addMultiActionInfo("cs12:CS1240003B_U3");
		tit_addMultiActionInfo("cs12:CS1240003B_D1");
		tit_addMultiActionInfo("cs12:CS1240003B_I8");
//		tit_addSingleActionInfo("cs12:CS1240001B_I5"); 

		tit_callService(
			""
			, ""
			, "ds_head=ds_head ds_list2=ds_list2 ds_detail_pri=ds_detail_pri"
			, ""
			, ""
			, "fn_afterSave"
			, true
			, false
			, true);
	}	
}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
	if(length(ds_head.GetColumn(0, "APSTAT")) > 0) {
		gfn_showAlert("결재상신후 삭제는 불가능합니다.");
		return false;
	}

	//if(G_USER_ID = "2020882") {
	if(vDel_auth == true) {

	} else {
		if(ds_head.GetColumn(0, "PST") > "A1") {
			gfn_showAlert("작성상태에서만 삭제가 가능합니다.");
			return false;
		}		
	}

	tit_clearActionInfo();

	tit_addSingleActionInfo("cs12:CS1240003B_U1");
	tit_addSingleActionInfo("cs12:CS1240003B_U7"); //재견적 이후 작성데이터 삭제시 이전승인데이터 복구
	tit_callService(
		""
		, ""
		, "ds_head=ds_head"
		, ""
		, ""
		, "fn_afterDelete"
		, true);

}

/********************************************************************************
* 기      능   : 재견적
********************************************************************************/
function fn_recost() {
	if(ds_head.GetColumn(0, "PST") != "A2" && ds_head.GetColumn(0, "PST") != "A6") {
		gfn_showAlert("재견적이 가능한 상태가 아닙니다.");
		return false;
	}
	if(ds_head.GetColumn(0, "STD") == "") {
		gfn_showAlert("계약시작일을 확인하십시요.");
		return false;
	}

	if(ds_head.GetColumn(0, "ETD") == "") {
		gfn_showAlert("계약종료일을 확인하십시요.");
		return false;
	}

	if(parseInt(ds_head.GetColumn(0, "ETD")) <= parseInt(ds_head.GetColumn(0, "STD"))){
		gfn_showAlert("계약기간을 확인하십시오.");
		return false;
	}
	if(ds_head.GetColumn(0, "CDT") == "") {
		gfn_showAlert("계약예정일을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CPD") == "") {
		gfn_showAlert("계약상품을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "NAM") == "") {
		gfn_showAlert("고객명을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SPT") == "") {
		gfn_showAlert("현장명을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CND") == "") {
		gfn_showAlert("계약유형을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "ADR") == "") {
		gfn_showAlert("현장주소를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "GBU") == "") {
		gfn_showAlert("직영구분을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CNT") == "") {
		gfn_showAlert("대수를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "FLR") == "") {
		gfn_showAlert("층수를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SPD") == "") {
		gfn_showAlert("속도를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "YGB") == "") {
		gfn_showAlert("영업주체를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "CYN") == "") {
		gfn_showAlert("CRT유무를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "PRD") == "") {
		gfn_showAlert("점검주기를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "BLD") == "") {
		gfn_showAlert("건물용도를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SRG") == "") {
		gfn_showAlert("Sheave/Rope를 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "PDT") == "") {
		gfn_showAlert("제품군을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "SYR") == "") {
		gfn_showAlert("설치연령을 확인하십시요.");
		return false;
	}
	if(ds_head.GetColumn(0, "RMK") == "") {
		gfn_showAlert("특이사항을 확인하십시요.");
		return false;
	}

	for(var i=0; i<ds_list2.rowcount; i++) {
		if(ds_head.GetColumn(0, "CPD") == "A" && ds_list2.GetColumn(0, "FMT") == 0 || length(trim(ds_list2.GetColumn(0, "FMT"))) == "") {
			gfn_showAlert("FM보수료는 필수입력 사항입니다.");
			return false;
		}

		if(ds_head.GetColumn(0, "CPD") == "B" && ds_list2.GetColumn(0, "PMT") == 0 || length(trim(ds_list2.GetColumn(0, "PMT"))) == "") {
			gfn_showAlert("POG보수료는 필수입력 사항입니다.");
			return false;
		}

		if(length(trim(ds_list2.GetColumn(0, "HWR"))) > 0 && ds_list2.GetColumn(0, "HMT") == 0) {
			gfn_showAlert("HRTS료는 필수입력 사항입니다.");
			return false;
		}

		if(length(trim(ds_list2.GetColumn(0, "DND"))) > 0 && ds_list2.GetColumn(0, "DMT") == 0) {
			gfn_showAlert("DI-PBX료는 필수입력 사항입니다.");
			return false;
		}

		ds_list2.SetColumn(i, "ETN", ds_head.GetColumn(0, "ETN"));
		ds_list2.SetColumn(i, "SIR", ds_head.GetColumn(0, "SIR"));

		if(parseInt(ds_list2.GetColumn(i, "FMT")) == 0 || length(trim(ds_list2.GetColumn(i, "FMT"))) == 0) {
			ds_list2.SetColumn(i, "FMT", 0);
			ds_list2.SetColumn(i, "FMG", "N");
		} else {
			ds_list2.SetColumn(i, "FMG", "Y");
		}

		if(parseInt(ds_list2.GetColumn(i, "PMT")) == 0 || length(trim(ds_list2.GetColumn(i, "PMT"))) == 0) {
			ds_list2.SetColumn(i, "PMT", 0);
			ds_list2.SetColumn(i, "POG", "N");
		} else {
			ds_list2.SetColumn(i, "POG", "Y");
		}

		if(parseInt(ds_list2.GetColumn(i, "HMT")) == 0 || length(trim(ds_list2.GetColumn(i, "HMT"))) == 0) {
			ds_list2.SetColumn(i, "HMT", 0);
			ds_list2.SetColumn(i, "HGB", "N");
		} else {
			if(length(ds_list2.GetColumn(i, "HWR")) == 0 ){ // 회선종류
				var arr = [parseInt(i) + 1, st_hwr.value];
				gfn_showMsg("CW00055", arr);
				return;
			} else {
				ds_list2.SetColumn(i, "HGB", "Y");
			}
		}

		if(parseInt(ds_list2.GetColumn(i, "DMT")) == 0 || length(trim(ds_list2.GetColumn(i, "DMT"))) == 0) {
			ds_list2.SetColumn(i, "DMT", 0);
			ds_list2.SetColumn(i, "DGB", "N");
		} else {
			if(length(ds_list2.GetColumn(i, "DND")) == 0 ){ // DI-PBX종류
				var arr = [parseInt(i) + 1, st_dnd.value];
				gfn_showMsg("CW00055", arr);
				return;
			} else {
				ds_list2.SetColumn(i, "DGB", "Y");
			}
		}

		if(length(trim(ds_list2.GetColumn(i, "HWR"))) > 0) {
			ds_head.SetColumn(0, "HYN", "Y");
		} else {
			ds_head.SetColumn(0, "HYN", "N");
		}

		if(length(trim(ds_list2.GetColumn(i, "DND"))) > 0) {
			ds_head.SetColumn(0, "DYN", "Y");
		} else {
			ds_head.SetColumn(0, "DYN", "N");
		}
	}

	var str_dat = 0.0;
	str_dat = round((parseInt(ds_head.GetColumn(0, "MMN")) / gfn_getMonthCnt(ds_head.GetColumn(0, "STD"), ds_head.GetColumn(0, "ETD"))) * 100,1);

	ds_head.SetColumn(0, "DAT", str_dat);

	if(ds_head.GetColumn(0, "GIS") = "") ds_head.SetColumn(0, "GIS", "90.0");

	if(!gfn_showConfirm("CI00024","재견적")) {
		return;
	}

	if(ds_detail_pri.rowcount <= 0) {
		fn_make_period();
	} else {
		var tot = toNumber(ds_list2.TotalCaseSum("AMT>0","AMT"));
		var tot2= toNumber(ds_detail_pri.TotalCaseSum("TOT>0","STOT"));;

		tot = tot * gfn_getMonthCnt(cal_from.Value,cal_to.Value);

		if(tot == 0 || tot2 == 0 || tot != tot2){
			alert('총계약금액('+tot+')과 ' + '계약기간 별 금액합('+tot2+')이 틀립니다. \n'
				+ '계약기간 별 금액을 확인하십시요.'
			);
			return false;
		}
	}
	if(ds_head.GetColumn(0, "PNT") = "") ds_head.SetColumn(0, "PNT", "0");
	if(ds_head.GetColumn(0, "SAJ") = "") ds_head.SetColumn(0, "SAJ", "0");
	if(ds_head.GetColumn(0, "GIS") = "") ds_head.SetColumn(0, "GIS", "90.0");


	ds_docno_gen.ClearData();													//결재번호생성 데이터셋을 클리어한다
	ds_docno_gen.AddRow();																
	ds_docno_gen.SetColumn(0,"PFX","H10");										//Prefix

	tit_clearActionInfo();					
	tit_addSearchActionInfo("wb01:DOC_CODE_GEN_S1",true);					
	tit_addSearchActionInfo("wb01:DOC_CODE_GEN_U1");					
	tit_callService(														//동기화 방식으로 서비스를 콜한다.	
		, ""				
		, "ds_docno_gen_i=ds_docno_gen"				
		, "ds_docno_gen=ds_docno_gen_o"				
		, ""				
		, ""				
		, true
		, false
		, true);				

	ds_head.SetColumn(0,"DOC",ds_docno_gen.GetColumn(0, "DOCNO"));
	/*
	ds_cond3.SetColumn(0, "DOC", ds_head.GetColumn(0, "DOC"));

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs12:CS1240003B_S14");
	tit_callService(
        ""
        , ""
        , "ds_cond3=ds_cond3"
        , "ds_cnt3=ds_cnt3"
        , ""
        , "fn_afterCnt"
        , true
        , true);
	*/
	fn_savRecost();
}

function fn_afterCnt(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		if(ds_cnt3.GetColumn(0, "CNT") > 0 && ds_head.GetColumn(0,"APSTAT") != '2' && ds_head.GetColumn(0,"APSTAT") != '4') {
			gfn_showAlert("이미 상신을 완료하여 재견적이 불가능 합니다.");
			return false;
		} else {
			fn_savRecost();
		}
	}
}

function fn_savRecost() {
	tit_clearActionInfo();

	tit_addSingleActionInfo("cs12:CS1240003B_U6");
	tit_addSingleActionInfo("cs12:CS1240003B_I3");
	tit_addMultiActionInfo("cs12:CS1240003B_I4");
	tit_addMultiActionInfo("cs12:CS1240003B_I7");
	tit_callService(
		""
		, ""
		, "ds_head=ds_head ds_list2=ds_list2 ds_detail_pri=ds_detail_pri"
		, ""
		, ""
		, "fn_afterRecost"
		, true);

}

/********************************************************************************
* 기      능   : 전송
********************************************************************************/
function fn_trans() {
	if(ds_head.GetColumn(0, "PST") > "A1" || length(trim(ds_head.GetColumn(0, "PST")))==0) {
		gfn_showAlert("작성상태에서만 전송이 가능합니다.");
		return false;
	}

/*
	if(trim(ds_head.GetColumn(0, "CPD")) == "A" && length(ds_head.GetColumn(0, "MASTAT")) == 0) {
		gfn_showAlert("자재입력후 전송 가능합니다.");
		return false;
	}

	if(length(ds_head.GetColumn(0, "APSTAT")) == 0) {
		gfn_showAlert("결재상신후 전송 가능합니다.");
		return false;
	}
*/
	if(!gfn_showConfirm("CI00024","전송")) {
		return;
	}

	ds_cost.ClearData();
	ds_cost_sum_o.ClearData();


	//내용을 다시 저장후 전송함.
	sendChk = false;
	if(ds_head.GetRowType(0)!='normal') {
		//alert('ds_head');
		sendChk = true;
	}
	for(i=0;i<ds_list2.GetRowCount();i++) {
		if(ds_list2.GetRowType(i)!='normal') {
			//alert('ds_list2');
			sendChk = true;
		}
	}
	for(i=0;i<ds_detail_pri.GetRowCount();i++) {
		if(ds_detail_pri.GetRowType(i)!='normal') {
			//alert('ds_detail_pri');
			sendChk = true;
		}
	}

	if(sendChk) {
		//gfn_showAlert("CW10025","수정된 내용이 있습니다. 저장을 먼저 하시고 [전송] 버튼을 클릭하세요.");
		//return false;
		//fn_save();
			if(length(ds_head.GetColumn(0, "APSTAT")) > 0) {
				gfn_showAlert("결재상신후 수정은 불가능합니다.");
				return false;
			}
			if(length(trim(ds_head.GetColumn(0, "ETN"))) == 0 && ds_head.GetColumn(0, "PST") >= "A1") {
				gfn_showAlert("진행상태를 확인하십시요.");
				return false;
			}
			if(length(trim(ds_head.GetColumn(0, "ETN"))) > 0 && (length(trim(ds_head.GetColumn(0, "PST"))) == 0 || ds_head.GetColumn(0, "PST") > "A2")) {
				gfn_showAlert("진행상태를 확인하십시요.");
				return false;
			}

			if(ds_head.GetColumn(0, "STD") == "") {
				gfn_showAlert("계약시작일을 확인하십시요.");
				return false;
			}

			if(ds_head.GetColumn(0, "ETD") == "") {
				gfn_showAlert("계약종료일을 확인하십시요.");
				return false;
			}

			if(parseInt(ds_head.GetColumn(0, "ETD")) <= parseInt(ds_head.GetColumn(0, "STD"))){
				gfn_showAlert("계약기간을 확인하십시오.");
				return false;
			}

			if(ds_head.GetColumn(0, "CDT") == "") {
				gfn_showAlert("계약예정일을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "CPD") == "") {
				gfn_showAlert("계약상품을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "NAM") == "") {
				gfn_showAlert("고객명을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "SPT") == "") {
				gfn_showAlert("현장명을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "CND") == "") {
				gfn_showAlert("계약유형을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "ADR") == "") {
				gfn_showAlert("현장주소를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "GBU") == "") {
				gfn_showAlert("직영구분을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "CNT") == "") {
				gfn_showAlert("대수를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "FLR") == "") {
				gfn_showAlert("층수를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "SPD") == "") {
				gfn_showAlert("속도를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "YGB") == "") {
				gfn_showAlert("영업주체를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "CYN") == "") {
				gfn_showAlert("CRT유무를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "PRD") == "") {
				gfn_showAlert("점검주기를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "BLD") == "") {
				gfn_showAlert("건물용도를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "SRG") == "") {
				gfn_showAlert("Sheave/Rope를 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "PDT") == "") {
				gfn_showAlert("제품군을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "SYR") == "") {
				gfn_showAlert("설치연령을 확인하십시요.");
				return false;
			}
			if(ds_head.GetColumn(0, "RMK") == "") {
				gfn_showAlert("특이사항을 확인하십시요.");
				return false;
			}

			for(var i=0; i<ds_list2.rowcount; i++) {		
				if(ds_head.GetColumn(0, "CPD") == "A" && (ds_list2.GetColumn(0, "FMT") == 0 || length(trim(ds_list2.GetColumn(0, "FMT"))) == "" )) {
					gfn_showAlert("FM보수료는 필수입력 사항입니다.");
					return false;
				}

				if(ds_head.GetColumn(0, "CPD") == "B" && (ds_list2.GetColumn(0, "PMT") == 0 || length(trim(ds_list2.GetColumn(0, "PMT"))) == "")) {
					gfn_showAlert("POG보수료는 필수입력 사항입니다.");
					return false;
				}

				if(length(trim(ds_list2.GetColumn(0, "HWR"))) > 0 && ds_list2.GetColumn(0, "HMT") == 0) {
					gfn_showAlert("HRTS료는 필수입력 사항입니다.");
					return false;
				}

				if(length(trim(ds_list2.GetColumn(0, "DND"))) > 0 && ds_list2.GetColumn(0, "DMT") == 0) {
					gfn_showAlert("DI-PBX료는 필수입력 사항입니다.");
					return false;
				}

				ds_list2.SetColumn(i, "ETN", ds_head.GetColumn(0, "ETN"));
				ds_list2.SetColumn(i, "SIR", ds_head.GetColumn(0, "SIR"));

				if(parseInt(ds_list2.GetColumn(i, "PDN")) == 0 || length(trim(ds_list2.GetColumn(i, "PDN"))) == 0) {
					ds_list2.SetColumn(i, "PDN", 0);
				} 

				if(parseInt(ds_list2.GetColumn(i, "FMT")) == 0 || length(trim(ds_list2.GetColumn(i, "FMT"))) == 0) {
					ds_list2.SetColumn(i, "FMT", 0);
					ds_list2.SetColumn(i, "FMG", "N");
				} else {
					ds_list2.SetColumn(i, "FMG", "Y");
				}

				if(parseInt(ds_list2.GetColumn(i, "PMT")) == 0 || length(trim(ds_list2.GetColumn(i, "PMT"))) == 0) {
					ds_list2.SetColumn(i, "PMT", 0);
					ds_list2.SetColumn(i, "POG", "N");
				} else {
					ds_list2.SetColumn(i, "POG", "Y");
				}

				if(parseInt(ds_list2.GetColumn(i, "HMT")) == 0 || length(trim(ds_list2.GetColumn(i, "HMT"))) == 0) {
					ds_list2.SetColumn(i, "HMT", 0);
					ds_list2.SetColumn(i, "HGB", "N");
				} else {
					if(length(ds_list2.GetColumn(i, "HWR")) == 0 ){ // 회선종류
						var arr = [parseInt(i) + 1, st_hwr.value];
						gfn_showMsg("CW00055", arr);
						return;
					} else {
						ds_list2.SetColumn(i, "HGB", "Y");
					}
				}

				if(parseInt(ds_list2.GetColumn(i, "DMT")) == 0 || length(trim(ds_list2.GetColumn(i, "DMT"))) == 0) {
					ds_list2.SetColumn(i, "DMT", 0);
					ds_list2.SetColumn(i, "DGB", "N");
				} else {
					if(length(ds_list2.GetColumn(i, "DND")) == 0 ){ // DI-PBX종류
						var arr = [parseInt(i) + 1, st_dnd.value];
						gfn_showMsg("CW00055", arr);
						return;
					} else {
						ds_list2.SetColumn(i, "DGB", "Y");
					}
				}

				if(length(trim(ds_list2.GetColumn(i, "HWR"))) > 0) {
					ds_head.SetColumn(0, "HYN", "Y");
				} else {
					ds_head.SetColumn(0, "HYN", "N");
				}

				if(length(trim(ds_list2.GetColumn(i, "DND"))) > 0) {
					ds_head.SetColumn(0, "DYN", "Y");
				} else {
					ds_head.SetColumn(0, "DYN", "N");
				}
			}

			if(length(trim(ds_head.GetColumn(0, "TXT"))) > 0 && ds_head.GetColumn(0, "TOT") < 1) {	
				gfn_showAlert("CW10025","자재금액이 없습니다. 자재금액을 입력하세요");
				ed_tot.SetFocus();
				return false;
			}

			if(length(trim(ds_head.GetColumn(0, "TXT"))) == 0 && ds_head.GetColumn(0, "TOT") > 0) {
				gfn_showAlert("CW10025","자재내역이 없습니다. 자재내역을 입력하세요");
				ed_txt.SetFocus();
				return false;
			}

			var str_dat = 0.0;
			str_dat = round((parseInt(ds_head.GetColumn(0, "MMN")) / gfn_getMonthCnt(ds_head.GetColumn(0, "STD"), ds_head.GetColumn(0, "ETD"))) * 100,1);

			ds_head.SetColumn(0, "DAT", str_dat);

			if(ds_detail_pri.rowcount <= 0) {
				fn_make_period();
			} else {
				var tot = toNumber(ds_list2.TotalCaseSum("AMT>0","AMT"));
				var tot2= toNumber(ds_detail_pri.TotalCaseSum("TOT>0","STOT"));;

				tot = tot * gfn_getMonthCnt(cal_from.Value,cal_to.Value);

				if(tot == 0 || tot2 == 0 || tot != tot2){
					alert('총계약금액('+tot+')과 ' + '계약기간 별 금액합('+tot2+')이 틀립니다. \n'
						+ '계약기간 별 금액을 확인하십시요.'
					);
					return false;
				}
			}
			if(ds_head.GetColumn(0, "PNT") = "") ds_head.SetColumn(0, "PNT", "0");
			if(ds_head.GetColumn(0, "SAJ") = "") ds_head.SetColumn(0, "SAJ", "0");
			if(ds_head.GetColumn(0, "GIS") = "") ds_head.SetColumn(0, "GIS", "90.0");	//기성지급율
			if(ds_head.GetColumn(0, "TOT") = "") ds_head.SetColumn(0, "TOT", "0");	

			if(length(trim(ds_head.GetColumn(0, "ETN"))) == 0) {	//견적테이블에 내역이 없는경우
				ds_docno_gen.ClearData();													//결재번호생성 데이터셋을 클리어한다
				ds_docno_gen.AddRow();																
				ds_docno_gen.SetColumn(0,"PFX","H10");										//Prefix

				tit_clearActionInfo();					
				tit_addSearchActionInfo("wb01:DOC_CODE_GEN_S1",true);					
				tit_addSearchActionInfo("wb01:DOC_CODE_GEN_U1");					
				tit_callService(															//동기화 방식으로 서비스를 콜한다.	
					, ""				
					, "ds_docno_gen_i=ds_docno_gen"				
					, "ds_docno_gen=ds_docno_gen_o"				
					, ""				
					, ""				
					, true
					, false
					, true);				

				ds_head.SetColumn(0,"DOC",ds_docno_gen.GetColumn(0, "DOCNO"));

				tit_clearActionInfo();
		//		tit_addSingleActionInfo("cs12:CS1240001B_U4");
				tit_addSingleActionInfo("cs12:CS1240003B_I1");
				tit_addMultiActionInfo("cs12:CS1240003B_I2");
				tit_addMultiActionInfo("cs12:CS1240003B_I6");
			} else {
				tit_clearActionInfo();
				tit_addSingleActionInfo("cs12:CS1240003B_U2");
				tit_addMultiActionInfo("cs12:CS1240003B_U3");
				tit_addMultiActionInfo("cs12:CS1240003B_D1");
				tit_addMultiActionInfo("cs12:CS1240003B_I8");
		//		tit_addSingleActionInfo("cs12:CS1240001B_I5"); 
			}


			tit_callService(
				""
				, ""
				, "ds_head=ds_head ds_list2=ds_list2 ds_detail_pri=ds_detail_pri"
				, ""
				, ""
				, "fn_afterSave"
				, true
				, false
				, true);
		//sendChk=false;
	}

	fn_HnoCostCal();
}

/********************************************************************************
* 기      능   : 시행율 계산
********************************************************************************/
function fn_saveRat() {

	tit_clearActionInfo();

	tit_addSingleActionInfo("cs12:CS1240003B_I5");
	tit_callService(
		""
		, ""
		, "ds_cost=ds_cost"
		, ""
		, ""
		, "fn_afterSaveRat"
		, true);

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");

		if(!sendChk) //전송으로 저장시 창을 닫지 않음
			if(lnk == "INS" || lnk == "PLN") {
				fn_selHno2();
			} else {
				fn_selHno();
			}
		}
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		close();
	}
}

/********************************************************************************
* 기      능   : 재견적 Callback
********************************************************************************/
function fn_afterRecost(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		close();
	}
}

/********************************************************************************
* 기      능   : 전송 Callback
********************************************************************************/
function fn_afterTrans(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		fn_saveRat();
	}
}

/********************************************************************************
* 기      능   : 시행율 Callback
********************************************************************************/
function fn_afterSaveRat(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00006", "전송");
		//close();
		fn_selHno();
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

