	gfn_openPopup("CS9601002A",true,arg);
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	if(nCell == 24){
		var arg = "lnk=UPD";
		gfn_openPopup("CS1240004C",true,arg);
	} else {
		var arg = "lnk=UPD";
		gfn_openPopup("CS1240003B",true,arg);
		fn_query();
	}
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;

	switch(strColumnID) {
		case "CHK":
			ds_list.SetColumn(nRow, "FLAG", "U");
			break;
	}

	obj.fireevent = true;
}

function btn_const_prt_OnClick(obj) {
	gfn_clearMsgBar();
//	ds_condP.ClearData();
//	ds_headP.ClearData();
//	ds_listP.ClearData();

	if(ds_list.rowCount() < 1) {
		// 출력할 정보 없음
		return;
	}

	if(!gfn_isChangeDs(ds_list, true)) {
		return;
	}

	var k = 0;
	var z = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) { //체크박스가 true 일 경우
			if(ds_list.GetColumn(i, "PST") <> "A3") {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				return false;
			}

			z = i;
			k++;
		}
	}

	if(k == 0 || k > 1) {
		gfn_showMsg("검토의뢰서 출력은 한건씩 가능합니다.");
		return false;
	}

	ds_condP.SetColumn(0, "TEM", substr(ds_list.GetColumn(z, "ETN"),0,3));
	ds_condP.SetColumn(0, "RDT", substr(ds_list.GetColumn(z, "ETN"),4,4));
	ds_condP.SetColumn(0, "SEQ", substr(ds_list.GetColumn(z, "ETN"),9,4));
	ds_condP.SetColumn(0, "SIR", ds_list.GetColumn(z, "SIR"));

    ds_apcond.SetColumn(0, "DOCNO", ds_list.GetColumn(z, "DOC"));
    ds_apcond.SetColumn(0, "DOCKIND", "21");

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1240004A_S3");
	tit_addSearchActionInfo("cs12:CS1240004A_S4");
	tit_addSearchActionInfo("cs12:CS1240004A_S5");
	tit_addSearchActionInfo("cs12:CS1240004A_S6");
	//tit_addSearchActionInfo("cs12:CS1241001A_S7");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_condP ds_apcond=ds_apcond"
        , "ds_headP=ds_headP ds_listR=ds_listR ds_listT=ds_listT ds_aplist=ds_aplist ds_listM=ds_listM"
        , ""
        , "fn_afterConstPrt"
        , true
        , true);
}

