function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	cal_fdt.Value = substr(gfn_getCurrDate(),0,6) + "01";
	cal_tdt.Value = gfn_getCurrDate();

	userGroup = gfn_getUserGroup();

	if(userGroup == '20' || userGroup == '30') {
		ufn_enableBtn("sendback", true);
		ufn_enableBtn("approval", true);
	} else {
		ufn_enableBtn("sendback", false);
		ufn_enableBtn("approval", false);
	}

	// if(G_USER_ID == "2021056" || G_USER_ID == "2017750" || G_USER_ID == "2021200" || 
	   // G_USER_ID == "2023687" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") { // 전현정
		// admin = true;
	// }
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS1241001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	admin = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

}
