/********************************************************************************
* 기      능   : 반송
********************************************************************************/
function fn_sendback() {
	gfn_clearMsgBar();

	if(ds_list.rowCount() < 1) {
		// 승인할 정보 없음.
		return;
	}

	ds_list2.ClearData();
	var rows = 0;

	for(var i=0; i<ds_list.rowcount; i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			if(ds_list.GetColumn(i, "PST") <> "A3") {
				var arr = [parseInt(i) + 1];
				gfn_showMsg("CW00042", arr);
				return;
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(rows, "FLAG", "U");
			ds_list2.SetColumn(rows, "ETN", ds_list.GetColumn(i, "ETN"));
			ds_list2.SetColumn(rows, "SIR", ds_list.GetColumn(i, "SIR"));

			rows++;
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs12:CS1241001A_U2"
		, ""
		, "FLAG"
		, ""
		, -1
		, "");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterSendback");
}

