function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var vHText = grd_list.GetCellProp("HEAD",nCell, "text");
	if (vHText == "비고"){						//if(nCell == 25){
		var arg = "lnk=UPD";
		gfn_openPopup("CS1241001C",true,arg);
	} else if (vHText == "결재문서번호") {
		var arg = "fa_docno=" + ds_list.GetColumn(nRow, "DOC");;		
		gfn_openPopup("CS0405001A", true, arg);	
	} else {
		var arg = "lnk=UPD";
		gfn_openPopup("CS1240001B",true,arg);
		if(G_USER_ID != "2018209") { // 임시 최선경
			fn_query();
		}
	}
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	obj.fireevent = false;

	switch(strColumnID) {
		case "CHK":
			ds_list.SetColumn(nRow, "FLAG", "U");
			break;
	}

	obj.fireevent = true;
}

