function fn_setCof(){
	//trace(ds_headP.SaveXML());

	var cnd = ds_headP.GetColumn(0,"CND");
	var cpd = ds_headP.GetColumn(0,"CPD");
	var cof = "";
	var cof2 =  ds_headP.GetColumn(0,"COF2");
	if(cnd == "Renewal" and cpd == "POG") {
		cof = ds_headP.GetColumn(0,"COF3");
	} else {
		cof = ds_headP.GetColumn(0,"COF");
	}

	var rank_cof, rank_cof2; //  달성율 , 할인율 

	if(cof=="본부장")      rank_cof = 1;
	else if(cof=="담당")   rank_cof = 2;
	else if(cof=="팀장") rank_cof = 3;
	else if(cof=="지사장") rank_cof = 4;

	if(cof2=="본부장")      rank_cof2 = 1;
	else if(cof2=="담당")   rank_cof2 = 2;
	else if(cof2=="팀장") rank_cof2 = 3;
	else if(cof2=="지사장") rank_cof2 = 4;

	if(rank_cof <= rank_cof2) {
		cof = cof;trace("cof");
	} else {
		cof = cof2;trace("cof2");
	}

	// cfo 전결 규정 대상 체크
	// 보수료 + hrts + dipbx + 상주 1천만원 초과 및 달성율 85 이하 
	var monthly_amt = 0 ;
	if(cpd == "FM") {
		monthly_amt = toNumber(ds_headP.GetColumn(0,"FMT")) + toNumber(ds_headP.GetColumn(0,"FMBT")) ;
	} else {
		monthly_amt = toNumber(ds_headP.GetColumn(0,"PMT")) + toNumber(ds_headP.GetColumn(0,"PMBT"));
	}
	var monthly_hmt = toNumber(ds_headP.GetColumn(0,"HMT")) ;
	var monthly_dmt = toNumber(ds_headP.GetColumn(0,"DMT")) ;
	var monthly_smt = toNumber(ds_headP.GetColumn(0,"SAJ")) ;
	var trt = toNumber(ds_headP.GetColumn(0,"TRT"));
	var total_monthly = monthly_amt+monthly_hmt+monthly_dmt+monthly_smt ;
/*	 trace("보수료  : " +monthly_amt);
	 trace("hrts  : " +monthly_hmt);
	 trace("dipbx  : " +monthly_dmt);
	 trace("상주  : " +monthly_smt);
	 trace("trt  : " +trt);
	 trace(monthly_amt+monthly_hmt+monthly_dmt+monthly_smt);
*/	
	if (total_monthly > 10000000 && trt <= 85) {
		// "(비고) 1천만원 초과 & 표준판가 기준 15% 이상 인하시, CFO 확인(월 계약금액 기준)" ;
		cof = "CFO 확인" ;
	}

	ds_headP.SetColumn(0,"COF",cof);
}
