function btn_cos_OnClick(obj)
{
	var arg = "t_etn=" + quote(ds_list.GetColumn( ds_list.currow, "ETN")) 
	+ " t_sir=" + quote(ds_list.GetColumn( ds_list.currow, "SIR")) 
	+ " t_doc=" + quote(ds_list.GetColumn( ds_list.currow, "DOC"))
	+ " c=" + quote(0);

	fn_setDsCostT() ;
	gfn_openPopup("CS1240001D",true,arg);
}
