/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs12:CS1241001C_S1");
	tit_addSearchActionInfo("cs12:CS1241001C_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_head=ds_head ds_list2=ds_list2"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");

}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	/* 필수값 확인 */
/*
	if(length(trim(ds_head.GetColumn(0, "HP"))) == 0) {
		gfn_showAlert("HP(휴대폰번호)를 확인하십시오.");
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "TEL"))) == 0) {
		gfn_showAlert("TEL(전화번호)을 확인하십시오.");
		return false;
	}
*/	
	if(ds_list2.GetRowCount() == 0) {
		gfn_showAlert("상담일지를 작성하십시오.");
		return false;
	}

	for(var i=0; i<ds_list2.rowcount; i++) {
		if(length(trim(ds_list2.GetColumn(i, "DAT"))) == 0) {
			gfn_showAlert(i + 1 +  "번째 행의 상담일자를 확인하십시오.");
			return false;
		}
		if(length(trim(ds_list2.GetColumn(i, "PNM"))) == 0) {
			gfn_showAlert(i + 1 +  "번째 행의 상담자를 확인하십시오.");
			return false;
		}
		if(length(trim(ds_list2.GetColumn(i, "SNM"))) == 0) {
			gfn_showAlert(i + 1 +  "번째 행의 상담대상자를 확인하십시오.");
			return false;
		}
		if(length(trim(ds_list2.GetColumn(i, "RMK"))) == 0) {
			gfn_showAlert(i + 1 + "번째 행의 상담내용을 확인하십시오.");
			return false;
		}
		/* 최초작성여부 체크 */
		if(length(trim(ds_head.GetColumn(0, "SNO"))) == 0) {
			ds_list2.SetColumn(i, "GBN", "A");
		} else{
			ds_list2.SetColumn(i, "SNO", ds_head.GetColumn(0, "SNO"));	
			ds_list2.SetColumn(i, "GBN", "X");	
		}
	}

	tit_clearActionInfo();

	tit_addSingleActionInfo("cs12:CS1241001C_I1");
	tit_addMultiActionInfo("cs12:CS1241001C_I2");

	tit_callService(
		""
		, ""
		, "ds_head=ds_head ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {	

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

