********************************************************************************/
function fn_excel() {
	gfn_saveToExcel(grd_list2, true);
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input()
{	
	if(ds_list2.GetRowCount() < 8) {
		gfn_addGridRow(grd_list2, -1, "FLAG");	
	} else {
		gfn_showAlert("상담일지의 최대 순번은 8번입니다.");
		return false;
	}

}
function grd_list2_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{	
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 290;
	cal_temp.Left = rect[0] + 100; 

	cal_temp.Visible = true;	

	if(nCell == 1) {
		cal_temp.value = ds_list2.getColumn(nRow, "DAT");
	}	

	cal_temp.SetFocus();
}

// 달력 처리 부분 
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText) {

	if(grd_list2.GetClickedCell() == 1) {
		ds_list2.SetColumn(ds_list2.row, "DAT", strText);
	}

	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj) {
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {

	ds_list3.ClearData();
	ds_list3.Reset();

	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list2.GetColumn(i,"SAV") == "Y") {

			var j = ds_list3.AddRow();

			ds_list3.SetColumn(j, "SNO",  ds_list2.GetColumn(i, "SNO")); // 상담번호 10자리
			ds_list3.SetColumn(j, "SEQ",  ds_list2.GetColumn(i, "SEQ")); // 상담순번 01~08
			ds_list3.SetColumn(j, "DAT",  ds_list2.GetColumn(i, "DAT"));
			ds_list3.SetColumn(j, "PNM",  ds_list2.GetColumn(i, "PNM"));
			ds_list3.SetColumn(j, "SNM",  ds_list2.GetColumn(i, "SNM"));
			ds_list3.SetColumn(j, "RMK2", ds_list2.GetColumn(i, "RMK"));

			ds_list3.SetColumn(j, "ETN",     ds_head.GetColumn(0, "ETN"));		
			ds_list3.SetColumn(j, "SIR",     ds_head.GetColumn(0, "SIR"));			
			ds_list3.SetColumn(j, "NAM",     ds_head.GetColumn(0, "NAM"));			
			ds_list3.SetColumn(j, "SPT",     ds_head.GetColumn(0, "SPT"));			
			ds_list3.SetColumn(j, "ADR",     ds_head.GetColumn(0, "ADR"));			
			ds_list3.SetColumn(j, "PJT",     ds_head.GetColumn(0, "PJT"));
			ds_list3.SetColumn(j, "SPC",     ds_head.GetColumn(0, "SPC"));
			ds_list3.SetColumn(j, "ZSPEC2",  ds_head.GetColumn(0, "ZSPEC2"));
			ds_list3.SetColumn(j, "CNT",     ds_head.GetColumn(0, "CNT"));
			ds_list3.SetColumn(j, "ZBEPJEO", ds_head.GetColumn(0, "ZBEPJEO"));
			ds_list3.SetColumn(j, "ISD",     ds_head.GetColumn(0, "ISD"));
			ds_list3.SetColumn(j, "BMT",     ds_head.GetColumn(0, "BMT"));
			ds_list3.SetColumn(j, "BLD_NM",  ds_head.GetColumn(0, "BLD_NM"));
			ds_list3.SetColumn(j, "HP",      ds_head.GetColumn(0, "HP"));
			ds_list3.SetColumn(j, "TEL",     ds_head.GetColumn(0, "TEL"));
			ds_list3.SetColumn(j, "RMK1",    ds_head.GetColumn(0, "RMK"));
		}	
	}
	//UbiReport Call
	Dialog("CS12::CS1241001D.xml", "");

}
